pt.HeadshotMulti * 1.05   wept.ConeMin = wept.ConeMin * (0.85/1.275)  wept.ConeMax = wept.ConeMax * 0.85      wept.Primary.ClipSize = 20   wept.FireModeNames = {"2-Round Burst", "Semi-Auto"}  wept.FireModeNames3D = {"BRST", "SEMI"}   wept.PrimaryAttack = function(self)  if not self:CanPrimaryAttack() then return end  local fm = self:GetFireMode()   self:SetNextPrimaryFire(CurTime() + self:GetFireDelay(fm == 1 and 2.55 or 1))  self:EmitFireSound()   if fm == 0 then  self:SetNextShot(CurTime())  self:SetShotsLeft(self.Primary.BurstShots)  else  self:TakeAmmo()  self:ShootBullets(self.Primary.Damage, self.Primary.NumShots, self:GetCone())  end   self.IdleAnimation = CurTime() + self:SequenceDuration()  end   wept.Think = function(self)  BaseClass.Think(self)   local shotsleft = self:GetShotsLeft()  if shotsleft > 0 and CurTime() >= self:GetNextShot() then  self:SetShotsLeft(shotsleft - 1)  self:SetNextShot(CurTime() + self:GetFireDelay(12))   if self:Clip1() > 0 and self:GetReloadFinish() == 0 then  self:EmitFireSound()  self:TakeAmmo()  self:ShootBullets(self.Primary.Damage, self.Primary.NumShots, self:GetCone())   self.IdleAnimation = CurTime() + self:SequenceDuration()  else  self:SetShotsLeft(0)  end  end          self:NextThink(CurTime())         return true  end      function wept:SetNextShot(nextshot)         self:SetDTFloat(5, nextshot)     end      function wept:GetNextShot()         return self:GetDTFloat(5)     end      function wept:SetShotsLeft(shotsleft)         self:SetDTInt(1, shotsleft)     end      function wept:GetShotsLeft()         return self:GetDTInt(1)     end end) branch.CollectiveName = "Mecca"