SWEP.PrintName = "'Purger' Antidote Handgun" SWEP.Description = "Fires AoE antidote bombs that cure poison and remove and protect from status effects." SWEP.Slot = 4 SWEP.SlotPos = 0  SWEP.Base = "weapon_zs_baseproj" local BaseClass = baseclass.Get("weapon_zs_baseproj")  SWEP.HoldType = "revolver"  SWEP.ViewModel = "models/weapons/cstrike/c_pist_deagle.mdl" SWEP.WorldModel = "models/weapons/w_pist_deagle.mdl" SWEP.ShowViewModel = false SWEP.ShowWorldModel = false  SWEP.UseHands = true  SWEP.CSMuzzleFlashes = false  SWEP.ReloadSound = Sound("weapons/pistol/pistol_reload1.wav")  SWEP.Primary.Delay = 1  SWEP.Primary.ClipSize = 20 SWEP.Primary.DefaultClip = 150 SWEP.Primary.Ammo = "Battery" SWEP.RequiredClip = 5  SWEP.WalkSpeed = SPEED_NORMAL  SWEP.ConeMax = 0 SWEP.ConeMin = 0  SWEP.ReloadSpeed = 0.43 SWEP.FireAnimSpeed = 1.3 SWEP.Heal = 9  SWEP.IronSightsPos = Vector(-5.95, 3, 2.75) SWEP.IronSightsAng = Vector(-0.15, -1, 2)  SWEP.AllowQualityWeapons = true SWEP.HasMedicalAura = true  SWEP.HideStat = {     Damage = true,     ResistanceAmmoAs = true,     ConeMax = true,     ConeMin = true }  GAMEMODE:SetPrimaryWeaponModifier(SWEP, WEAPON_MODIFIER_CLIP_SIZE, 5) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_HEALING, 1)  function SWEP:EmitFireSound()  self:EmitSound("items/smallmedkit1.wav", 70, math.random(135, 140), 0.65, CHAN_WEAPON + 21)  self:EmitSound("weapons/galil/galil-1.wav", 75, math.random(122, 128), 0.7, CHAN_WEAPON + 20) end  function SWEP:EmitReloadSound()  if IsFirstTimePredicted() then  self:EmitSound("weapons/357/357_reload1.wav", 75, 75, 1, CHAN_WEAPON + 21)  end end  function SWEP:EmitReloadFinishSound()  if IsFirstTimePredicted() then  self:EmitSound("weapons/357/357_spin1.wav", 70, 90)  end end  function SWEP:SendReloadAnimation()  self:SendWeaponAnim(ACT_VM_DRAW) end