0) local colWhite = Color(220, 220, 220, 230) local colAmmo = Color(255, 255, 255, 230)  local function GetAmmoColor(clip, maxclip)  if clip == 0 then  colAmmo.r = 255 colAmmo.g = 0 colAmmo.b = 0  else  local sat = clip / maxclip  colAmmo.r = 255  colAmmo.g = sat ^ 0.3 * 255  colAmmo.b = sat * 255  end end  function SWEP:Draw2DHUD()  local screenscale = BetterScreenScale()  local wid, hei = 180 * screenscale, 160 * screenscale  local x, y = ScrW() - wid - screenscale * 128, ScrH() - hei - screenscale * 72  local clip = self:Clip1()  local spare = self:GetOwner():GetAmmoCount(self:GetPrimaryAmmoType())  local spare2 = math.floor(self.Owner:GetAmmoCount(self:GetSecondaryAmmoType()) /self.MedAmmo)  local maxclip = self.Primary.ClipSize  local scndclip = math.floor(self:Clip2() /self.MedAmmo)   if self.RequiredClip ~= 1 then  clip = math.floor(clip / self.RequiredClip)  spare = math.floor(spare / self.RequiredClip)  maxclip = math.ceil(maxclip / self.RequiredClip)  end   draw.RoundedBox(16, x, y, wid, hei, colBG)   local displayspare = maxclip > 0 and self.Primary.DefaultClip ~= 99999   if displayspare then  draw.SimpleTextBlurry(spare, spare >= 1000 and "ZSHUDFontSmall" or "ZSHUDFont", x + wid * 0.75, y + hei * 0.5, spare == 0 and colRed or spare <= maxclip and colYellow or colWhite, TEXT_ALIGN_CENTER, TEXT_ALIGN_BOTTOM)  end   draw.SimpleTextBlurry(spare2, spare2 >= 1000 and "ZSHUDFontSmall" or "ZSHUDFont", x + wid * 0.75, y + hei * 0.5, Color(0,255,0,255), TEXT_ALIGN_CENTER, TEXT_ALIGN_TOP)  GetAmmoColor(clip, maxclip)  draw.SimpleTextBlurry(clip, clip >= 100 and "ZSHUDFont" or "ZSHUDFontBig", x + wid * (displayspare and 0.25 or 0.5), y + hei * 0.5, colAmmo, TEXT_ALIGN_CENTER, TEXT_ALIGN_BOTTOM)  draw.SimpleTextBlurry(scndclip, scndclip >= 100 and "ZSHUDFont" or "ZSHUDFontBig", x + wid * (displayspare and 0.25 or 0.5), y + hei * 0.5, Color(0,255,0,255), TEXT_ALIGN_CENTER, TEXT_ALIGN_TOP)   local fm = self:GetFireMode()     if self.FireModes > 1 then         local fmname = self.FireModeNames[fm + 1] or "-"         draw.SimpleTextBlurry(fmname, "ZSHUDFont", x + wid * 0.5, y + hei + 32 * screenscale, colWhite, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)     end end  function SWEP:Draw3DHUD(vm, pos, ang)  local wid, hei = 180, 200  local x, y = wid * -0.6, hei * -0.5  local clip = self:Clip1()  local spare = self:GetOwner():GetAmmoCount(self:GetPrimaryAmmoType())  local maxclip = self.Primary.ClipSize  local spare2 = math.floor(self.Owner:GetAmmoCount(self:GetSecondaryAmmoType()) /self.MedAmmo)  local scndclip = math.floor(self:Clip2() /self.MedAmmo)   local dclip, dspare, dmaxclip = self:GetDisplayAmmo(clip, spare, maxclip)   cam.Start3D2D(pos, ang, self.HUD3DScale / 2)  draw.RoundedBoxEx(32, x, y, wid, hei * 1.7, colBG, true, false, true, false)   local displayspare = dmaxclip > 0 and self.Primary.DefaultClip ~= 99999  if displayspare then  draw.SimpleTextBlurry(dspare, dspare >= 1000 and "ZS3D2DFontSmall" or "ZS3D2DFont", x + wid * 0.5, y + hei * 0.75, dspare == 0 and colRed or dspare <= dmaxclip and colYellow or colWhite, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)  end   GetAmmoColor(dclip, dmaxclip)  draw.SimpleTextBlurry(dclip, dclip >= 100 and "ZS3D2DFont" or "ZS3D2DFontBig", x + wid * 0.5, y + hei * (displayspare and 0.3 or 0.5), colAmmo, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)  draw.SimpleTextBlurry(scndclip, scndclip >= 100 and "ZS3D2DFont" or "ZS3D2DFontBig", x + wid * 0.5, y + hei * 0.75, Color(0,255,0,255), TEXT_ALIGN_CENTER, TEXT_ALIGN_TOP)  draw.SimpleTextBlurry(spare2, spare2 >= 1000 and "ZS3D2DFontSmall" or "ZS3D2DFont", x + wid * 0.5, y + hei * 1.3, Color(0,255,0,255), TEXT_ALIGN_CENTER, TEXT_ALIGN_TOP)   local fm = self:GetFireMode()         if self.FireModes > 1 then             local fmname = self.FireModeNames3D[fm + 1] or "-"             draw.SimpleTextBlurry(fmname, "ZS3D2DFontSmall", x + wid * 0.5, y - 32, colWhite, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)         end  cam.End3D2D() end  function SWEP:ShouldDisplayMedicalAura()     return self:GetFireMode() == 1 end  function SWEP:HealingFunction(owner, pl)     owner:HealPlayer(pl, self.Heal) end  function SWEP:HealingCheck(pl)     return gamemode.Call("PlayerCanBeHealed", pl) end  function SWEP:SecondaryAttack()  local fm = self:GetFireMode()  if fm == 1 then  if self:CanSecondaryAttack() and self:Clip2() >= self.MedAmmo then  self:SetNextSecondaryFire(CurTime() + self.Secondary.Delay)   local owner = self:GetOwner()   self:SendWeaponAnimation()  owner:DoAttackEvent()  self:TakeSecondaryAmmo(self.MedAmmo)  self.IdleAnimation = CurTime() + self:SequenceDuration()  self:EmitSound("weapons/crossbow/fire1.wav", 75, 150, 1, CHAN_WEAPON + 20)           local trtbl = owner:CompensatedPenetratingMeleeTrace(self.HealRange, 2, nil, nil, true)          local heal_ents = {}          for _, tr in pairs(trtbl) do              local test = tr.Entity              if test and test:IsValidLivingHuman() and self:HealingCheck(test) then                  heal_ents[#heal_ents + 1] = test                   self:HealingFunction(owner, test)                   local effectdata = EffectData()                      effectdata:SetOrigin(test:WorldSpaceCenter())                      effectdata:SetFlags(3)                      effectdata:SetEntity(self)                      effectdata:SetAttachment(1)                  util.Effect("tracer_healray_light", effectdata)                   test:EmitSound("items/medshot4.wav", 75, 150, 1, CHAN_WEAPON + 20)                   if #heal_ents >= 3 then break end              end          end  end  else  BaseClass.SecondaryAttack(self)  end end  function SWEP:EmitFireSound()  self:EmitSound(self.Primary.Sound, 75, math.Rand(140,150)) end  function SWEP:FireModeChanged(fm) end  function SWEP:PlayFireModeSwitchSound(fm)     fm = tonumber(fm)     if CLIENT then         if fm == 1 then             surface.PlaySound("items/medshot4.wav")         else             surface.PlaySound("weapons/smg1/switch_burst.wav")         end     end 