elf.FireAnimSpeed = 0.2             self:ShootBullets(self.Primary.Damage, self.Primary.NumShots * 2, self:GetCone())             self.IdleAnimation = CurTime() + self:SequenceDuration()             self.FireAnimSpeed = 1              self:SetNextFires(self:GetNextFires() - 1)             self:GetNextFiringTick(CurTime() + 0.06)              if fires == 3 then                 self:EmitFireSound(fires == 3)             end         end     end      function wept:Initialize()         BaseClass.Initialize(self)          self.ChargeSound = CreateSound(self, "npc/strider/charging.wav")     end      function wept:PrimaryAttack()         if not self:CanPrimaryAttack() or self:GetNextChargeFinish() ~= 0 then return end          self:SetNextPrimaryFire(CurTime() + self:GetFireDelay())         self:SetNextChargeFinish(CurTime() + self:GetChargeTime())         self:TakeAmmo()     end      function wept:Reload()         local charge_finish = self:GetNextChargeFinish()         local fires = self:GetNextFires()          if fires >= 1 or charge_finish ~= 0 then return end          return BaseClass.Reload(self)     end      function wept:Holster()         if self:GetNextFires() > 0 or self:GetNextChargeFinish() > 0 then             return false         end          return BaseClass.Holster(self)     end      function wept:CanReload()         if self:GetNextFires() > 0 or self:GetNextChargeFinish() > 0 then             return false         end          return BaseClass.CanReload(self)     end      function wept:TakeAmmo()         return BaseClass.TakeAmmo(self)     end      function wept:Think()         self:CheckCharge()          return BaseClass.Think(self)     end      function wept:SecondaryAttack()         return BaseClass.SecondaryAttack(self)     end      function wept:SendWeaponAnimation()         return BaseClass.SendWeaponAnimation(self)     end      function wept:EmitFireSound(secondary)         local add = secondary and 3 or 0          if not secondary then             self:EmitSound("weapons/sg552/sg552-1.wav", 80, 65, 0.9, CHAN_WEAPON + 6)         end          self:EmitSound("weapons/zs_asmd/main3.ogg", 80, 80, 0.9, CHAN_WEAPON + add)         self:EmitSound("weapons/zs_dds/zs_raygun.ogg", 80, 55, 0.75, CHAN_WEAPON + 1 + add)         self:EmitSound("weapons/zs_dds/zs_raygun.ogg", 80, 56, 0.75, CHAN_WEAPON + 2 + add)     end      function wept:EmitReloadSound()         if IsFirstTimePredicted() then             self:EmitSound("vehicles/tank_readyfire1.wav", 75, 60, 1, CHAN_WEAPON + 21)             self:EmitSound("weapons/zs_dds/zs_rayre.ogg", 75, 74, 0.5, CHAN_WEAPON + 22)         end     end      wept.ViewModelBoneMods = {  ["ValveBiped.Bip01_L_Hand"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) }  }      wept.WalkSpeed = SPEED_SLOWEST * 0.85      if CLIENT then         local ghostlerp = 0         function wept:CalcViewModelView(vm, oldpos, oldang, pos, ang)             if self:GetOwner():GetBarricadeGhosting() or self:GetReloadFinish() > 0 then                 ghostlerp = math.min(1, ghostlerp + FrameTime() * 1)             elseif ghostlerp > 0 then                 ghostlerp = math.max(0, ghostlerp - FrameTime() * 1.5)             end              if ghostlerp > 0 then                 ang:RotateAroundAxis(ang:Right(), -20 * ghostlerp)             end              return pos, ang         end     end      function wept.BulletCallback(attacker, tr, dmginfo)         local dist = 22500         local leng = math.Clamp(tr.HitPos:DistToSqr(tr.StartPos) - 40000, 0, dist)         local base_damage = dmginfo:GetDamage()         local adj_damage  = base_damage * ((dist - leng)/dist)          dmginfo:SetDamage(base_damage * 0.6 + adj_damage * 0.4)          local ent = tr.Entity         if ent:IsValidLivingZombie() and ent:GetBossTier() >= 1 then             dmginfo:ScaleDamage(0.4)         end     end end) branch.CollectiveName = "Hyperion" branch.Killicon = "weapon_zs_slaser"  SWEP.Tier = 5  SWEP.ASMD = true  SWEP.TracerName = "tracer_cosmos"  SWEP.Taper = 0.83 SWEP.Radius = 100  function SWEP:SendWeaponAnimation()  self:SendWeaponAnim(ACT_VM_PRIMARYATTACK)  self:GetOwner():GetViewModel():SetPlaybackRate(self.FireAnimSpeed * 0.35)   timer.Simple(0.2, function()  if IsValid(self) then  self:SendWeaponAnim(ACT_VM_DRAW)  self:GetOwner():GetViewModel():SetPlaybackRate(self.FireAnimSpeed * 10.5)  end  end) end  function SWEP:CanPrimaryAttack()  if self:GetOwner():IsHolding() or self:GetOwner():GetBarricadeGhosting() or self:GetReloadFinish() > 0 then return false end   if self:Clip1() < 2 then  self:EmitSound(self.DryFireSound)  self:SetNextPrimaryFire(CurTime() + math.max(0.25, self.Primary.Delay))  return false  end   return self:GetNextPrimaryFire() <= CurTime() end  function SWEP:EmitFireSound(secondary)  self:EmitSound(secondary and "weapons/zs_asmd/secondary2.ogg" or "weapons/zs_asmd/main3.ogg", 75, math.random(105, 110))  self:EmitSound("weapons/zs_inner/innershot.ogg", 72, 231, 0.45, CHAN_AUTO) end  function SWEP:TakeAmmo(secondary)  self:TakePrimaryAmmo(secondary and 5 or 2) end  function SWEP:SecondaryAttack()  if self:Clip1() < 5 or not self:CanPrimaryAttack() then return end   self:SetNextPrimaryFire(CurTime() + self:GetFireDelay() * 1.35)  self:EmitFireSound(true)  self:TakeAmmo(true)   if SERVER then  self:ShootSecondary(self.Primary.Damage * 1.67, 1, self:GetCone()/3)  end   self.IdleAnimation = CurTime() + self:SequenceDuration() end