SWEP.PrintName = "Giant Zombie Kung Fu"  SWEP.Base = "weapon_zs_zombie" local BaseClass = baseclass.Get("weapon_zs_zombie")  SWEP.MeleeDelay = 0.32 SWEP.MeleeReach = 70 SWEP.MeleeSize = 16 SWEP.MeleeDamage = 35  SWEP.DelayWhenDeployed = false  function SWEP:PrimaryAttack(fromsecondary)  local n = self:GetNextPrimaryAttack()   local owner = self:GetOwner()  BaseClass.PrimaryAttack(self)   if not fromsecondary and n ~= self:GetNextPrimaryAttack() then  self:SetDTBool(3, false)  end end  function SWEP:SecondaryAttack()  local n = self:GetNextPrimaryAttack()  self:PrimaryAttack(true)  if n ~= self:GetNextPrimaryAttack() then  self:SetDTBool(3, true)  end end  function SWEP:PlayHitSound()  self:EmitSound("npc/zombie/zombie_pound_door.wav", 77, 65, nil, CHAN_AUTO) end  function SWEP:PlayAttackSound()  self:EmitSound("npc/zombie/foot_slide"..math.random(3)..".wav", 77, 65, nil, CHAN_AUTO) end  function SWEP:Reload() end  function SWEP:StartMoaning() end  function SWEP:StopMoaning() end  function SWEP:IsMoaning()  return false end 