AddCSLuaFile()  SWEP.PrintName = "Axe" SWEP.Description = "A simple axe, with balanced attributes across the board. Holding down attack performs a charged attack that hits multiple zombies."  if CLIENT then  SWEP.ViewModelFOV = 55  SWEP.ViewModelFlip = false   SWEP.ShowViewModel = false  SWEP.ShowWorldModel = false  SWEP.VElements = {  ["base"] = { type = "Model", model = "models/props/cs_militia/axe.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "", pos = Vector(3, 1.299, -4), angle = Angle(0, 0, 90), size = Vector(1, 1, 1), color = Color(255, 255, 255, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} }  }  SWEP.WElements = {  ["base"] = { type = "Model", model = "models/props/cs_militia/axe.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "", pos = Vector(3, 1.399, -4), angle = Angle(0, 0, 90), size = Vector(1, 1, 1), color = Color(255, 255, 255, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} }  } end  SWEP.Base = "weapon_zs_basemelee"  SWEP.ViewModel = "models/weapons/c_stunstick.mdl" SWEP.WorldModel = "models/props/cs_militia/axe.mdl" SWEP.UseHands = true  SWEP.HoldType = "melee2"  SWEP.MeleeDamage = 49 SWEP.MeleeRange = 59 SWEP.MeleeSize = 1.5 SWEP.MeleeKnockBack = 125  SWEP.WalkSpeed = SPEED_FAST  SWEP.SwingTime = 0.6 SWEP.SwingRotation = Angle(0, -20, -40) SWEP.SwingOffset = Vector(10, 0, 0) SWEP.SwingHoldType = "melee"  SWEP.BlockRotation = Angle(0, 40, -20) SWEP.BlockOffset = Vector(-10, 7, -5)  SWEP.Stability = 50 SWEP.BlockReduction = 0.6  SWEP.HitDecal = "Manhackcut"  SWEP.AllowQualityWeapons = true  SWEP.OverkillFactor = 0.5  SWEP.CleaveTaper = 0.5  SWEP.StaminaUsage = 0.16  SWEP.AlwaysCanCharge = true  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_MELEE_RANGE, 3) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.03) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_STAMINA_USAGE, -0.01) local branch = GAMEMODE:AddNewRemantleBranch(SWEP, 1, "Sawhack", "Decreased attack delay and swing time, but reduced range and damage", function(wept)  wept.Primary.Delay  = wept.Primary.Delay * 0.5  wept.SwingTime     = wept.SwingTime * 0.3  wept.MeleeDamage     = wept.MeleeDamage * 0.8  wept.MeleeRange     = wept.MeleeRange * (51/58)     wept.StaminaUsage       = wept.StaminaUsage * (0.135/0.16)     wept.OverkillFactor     = nil     wept.CleaveTaper        = nil   wept.VElements = {  ["axe"] = { type = "Model", model = "models/props/cs_militia/axe.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "", pos = Vector(3.184, 1.501, -7.421), angle = Angle(2.427, -10, 90), size = Vector(1, 1, 1), color = Color(255, 255, 255, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} },  ["saw"] = { type = "Model", model = "models/props_junk/sawblade001a.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "axe", pos = Vector(0, 14, -0.021), angle = Angle(0, 0, 0), size = Vector(0.449, 0.449, 0.805), color = Color(255, 255, 255, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} },  ["saw2"] = { type = "Model", model = "models/XQM/Rails/trackball_1.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "axe", pos = Vector(0, 14, 0), angle = Angle(0, 90, 0), size = Vector(0.234, 0.234, 0.133), color = Color(255, 255, 255, 255), surpresslightning = false, material = "models/props_lab/door_klab01", skin = 0, bodygroup = {} }  }   wept.WElements = {  ["axe"] = { type = "Model", model = "models/props/cs_militia/axe.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "", pos = Vector(3.023, 2.147, -8.32), angle = Angle(-6.166, 20.881, 86.675), size = Vector(1, 1, 1), color = Color(255, 255, 255, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} },  ["saw2"] = { type = "Model", model = "models/XQM/Rails/trackball_1.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "axe", pos = Vector(0, 14, 0), angle = Angle(0, 90, 0), size = Vector(0.234, 0.234, 0.133), color = Color(255, 255, 255, 255), surpresslightning = false, material = "models/props_lab/door_klab01", skin = 0, bodygroup = {} },  ["saw"] = { type = "Model", model = "models/props_junk/sawblade001a.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "axe", pos = Vector(0, 14, -0.021), angle = Angle(0, 0, 0), size = Vector(0.449, 0.449, 0.805), color = Color(255, 255, 255, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} }  }   wept.ShowViewModel = false  wept.ShowWorldModel = false   wept.HoldType = "melee2"  wept.SwingHoldType = "melee2"   function wept:PlaySwingSound()  self:EmitSound("weapons/iceaxe/iceaxe_swing1.wav", 75, math.random(75, 80))  end   function wept:PlayHitSound()  self:EmitSound("npc/manhack/grind"..math.random(5)..".wav")  end   function wept:PlayHitFleshSound()  self:EmitSound("ambient/machines/slicer"..math.random(4)..".wav")  end   function wept:OnMeleeHit(hitent, hitflesh, tr)  if not hitflesh then  local effectdata = EffectData()  effectdata:SetOrigin(tr.HitPos)  effectdata:SetNormal(tr.HitNormal)  effectdata:SetMagnitude(2)  effectdata:SetScale(1)  util.Effect("sparks", effectdata)  end  end      function wept:BeforeSwing(dmgmul)         return dmgmul     end   wept.HitDecal = "Manhackcut"  wept.HitAnim = ACT_VM_MISSCENTER   wept.BlockRotation = Angle(0, 40, -40)  wept.BlockOffset = Vector(-10, 7, 5)   wept.NoHitSoundFlesh = true end) branch.CollectiveName = "Razorhack" branch.Killicon       = "weapon_zs_sawhack"  function SWEP:PlaySwingSound()  self:EmitSound("weapons/iceaxe/iceaxe_swing1.wav", 75, math.random(65, 70)) end  function SWEP:PlayHitSound()  self:EmitSound("weapons/melee/golf club/golf_hit-0"..math.random(4)..".ogg") end  function SWEP:PlayHitFleshSound()  self:EmitSound("physics/body/body_medium_break"..math.random(2, 4)..".wav") end  function SWEP:BeforeSwing(dmgmul)  self.Cleave = self:IsHeavy()   return dmgmul * (self:IsHeavy() and 0.8 or 1) end 