AddCSLuaFile()  SWEP.Base = "weapon_zs_baseshotgun" local BaseClass = baseclass.Get("weapon_zs_baseshotgun")  SWEP.PrintName = "Boom Stick" SWEP.Description = "This shotgun allows you to load up to four shells in the chamber at once. Hold down reload for faster loading of each shell."  if CLIENT then  SWEP.HUD3DBone = "ValveBiped.Gun"  SWEP.HUD3DPos = Vector(1.75, 0, -8)  SWEP.HUD3DScale = 0.025   SWEP.ViewModelFOV = 56   SWEP.ViewModelFlip = false end  SWEP.ViewModel = "models/weapons/c_shotgun.mdl" SWEP.WorldModel = "models/weapons/w_shotgun.mdl" SWEP.UseHands = true  SWEP.CSMuzzleFlashes = false  SWEP.ReloadDelay = 0.5  SWEP.Primary.Sound = Sound("weapons/shotgun/shotgun_dbl_fire.wav") SWEP.Primary.Damage = 27 SWEP.Primary.NumShots = 6 SWEP.Primary.Delay = 1  SWEP.Recoil = 7.5  SWEP.Primary.ClipSize = 4 SWEP.Primary.Automatic = false SWEP.Primary.Ammo = "buckshot" SWEP.Primary.DefaultClip = 28  SWEP.ConeMax = 11.5 SWEP.ConeMin = 10  SWEP.Tier = 5  SWEP.WalkSpeed = SPEED_SLOWER SWEP.FireAnimSpeed = 0.4 SWEP.Knockback = 82  SWEP.PumpActivity = ACT_SHOTGUN_PUMP SWEP.PumpSound = Sound("weapons/shotgun/shotgun_cock.wav") SWEP.ReloadSound = Sound("weapons/shotgun/shotgun_reload1.wav")  SWEP.AmmoTrinketOverride = 2  SWEP.FireModes = 2 SWEP.FireModeNames = {"Full Load", "Semi-Auto"} SWEP.FireModeNames3D = {"BOOM", "SEMI"}  SWEP.GibThreshold = 16  GAMEMODE:SetPrimaryWeaponModifier(SWEP, WEAPON_MODIFIER_RELOAD_SPEED, 0.05, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_CLIP_SIZE, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_KNOCKBACK, 6, 1) GAMEMODE:AddNewRemantleBranch(SWEP, 1, "Lithe Stick", "Decreased damage but faster reload and more move speed", function(wept)  wept.Primary.Damage = wept.Primary.Damage * 0.75  wept.ReloadSpeed = wept.ReloadSpeed * 1.25  wept.Primary.Delay = wept.Primary.Delay * 0.75  wept.WalkSpeed = SPEED_NORMAL   wept.PreDrawViewModel = function(self, vm)  render.SetColorModulation(0.6, 0.6, 0.8)   BaseClass.PreDrawViewModel(self, vm)  end  wept.PostDrawViewModel = function(self, vm)  render.SetColorModulation(1, 1, 1)   BaseClass.PostDrawViewModel(self, vm)  end   function wept:StopReloading()  BaseClass.StopReloading(self)   self:SetNextPrimaryFire(CurTime() + self.Primary.Delay * 0.5)  end end).CollectiveName = "Lithe"  function SWEP:PrimaryAttack()  if not self:CanPrimaryAttack() then return end   local owner = self:GetOwner()  local fm = self:GetFireMode()   self:SetNextPrimaryFire(CurTime() + self:GetFireDelay())  self:EmitFireSound()   local clip = fm == 1 and 1 or self:Clip1()   self:ShootBullets(self.Primary.Damage, self.Primary.NumShots * clip, self:GetCone())   self:TakePrimaryAmmo(clip)   if fm ~= 1 then  owner:ViewPunch(clip * 0.5 * self.Recoil * Angle(math.Rand(-0.1, -0.1), math.Rand(-0.1, 0.1), 0))  owner:SetGroundEntity(NULL)  owner:SetVelocity(-self.Knockback * clip * owner:GetAimVector())  end   self.IdleAnimation = CurTime() + self:SequenceDuration() end  function SWEP:EmitFireSound()  local fm = self:GetFireMode()   if fm == 1 then  self:EmitSound(")weapons/shotgun/shotgun_fire62.wav", 75, math.random(95, 105))  else  self:EmitSound(")weapons/shotgun/shotgun_dbl_fire.wav", 75, math.random(95, 105))  end end  function SWEP:PlayFireModeSwitchSound(fm)  fm = tonumber(fm)  if CLIENT then  if fm == 1 then  surface.PlaySound("weapons/shotgun/shotgun_empty.wav")  else  surface.PlaySound("weapons/shotgun/shotgun_empty.wav")  surface.PlaySound("weapons/shotgun/shotgun_cock.wav")  end  end end  function SWEP:FireModeChanged(fm)  fm = tonumber(fm)  self.Recoil = fm ~= 1 and 7.5 or 0  self.SpreadPattern = fm ~= 0 and {  {0, 0.15},  {180, 0.15},  {0, 0.45},  {90, 0.4},  {180, 0.45},  {270, 0.4}  }  or nil end 