SWEP.PrintName = "'Broadside' Missile Launcher" SWEP.Description = "Launches missiles that deal considerable AOE explosive damage. Right click remotely detonates them."  SWEP.UseHands = false  SWEP.ShowViewModel = false SWEP.ShowWorldModel = false  SWEP.Base = "weapon_zs_baseproj" local BaseClass = baseclass.Get("weapon_zs_baseproj")  SWEP.HoldType = "rpg"  SWEP.ViewModel = "models/weapons/c_rpg.mdl" SWEP.WorldModel = "models/weapons/w_rocket_launcher.mdl"  SWEP.Primary.Delay = 1.2 SWEP.Primary.ClipSize = 3 SWEP.Primary.Automatic = false SWEP.Primary.Ammo = "impactmine" SWEP.Primary.DefaultClip = 3 SWEP.Primary.Damage = 101  SWEP.ReloadSound = Sound("vehicles/tank_readyfire1.wav") SWEP.Primary.Sound = Sound("weapons/stinger_fire1.wav")  SWEP.ReloadSpeed = 0.55 SWEP.Recoil = 3  SWEP.ConeMin = 0.0001 SWEP.ConeMax = 0.0001  SWEP.Primary.Gesture = ACT_HL2MP_GESTURE_RANGE_ATTACK_RPG SWEP.ReloadGesture = ACT_HL2MP_GESTURE_RELOAD_RPG  SWEP.WalkSpeed = SPEED_SLOWEST * 0.85  SWEP.FireAnimSpeed = 0.75  SWEP.Tier = 5  SWEP.Taper = 0.86 SWEP.Radius = 78  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_CLIP_SIZE, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.05, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_RADIUS, 3, 1)  GAMEMODE:AddNewRemantleBranch(SWEP, 1, "'Salvo' Micro-missile Launcher", "Rapidly fires miniature missiles that use 33% ammo", function(wept)  wept.Primary.Delay = wept.Primary.Delay * 0.3  wept.Primary.Automatic = true  wept.Recoil = 0  wept.Primary.Damage = wept.Primary.Damage * 0.3  wept.FireAnimSpeed = 1.1     wept.Taper = 0.8   wept.EmitFireSound = function(self)  self:EmitSound(self.Primary.Sound, 75, math.random(208, 213), 0.6)  self:EmitSound("weapons/grenade_launcher1.wav", 75, math.random(116, 122), 0.5, CHAN_WEAPON + 20)  end  wept.EntModify = function(self, ent)  ent:SetDTInt(0, 1)   local owner = self:GetOwner()  owner.RemoteDetRocket = ent   self:SetNextSecondaryFire(CurTime() + 0.2)  end   wept.PrimaryAttack = function(self)  if not self:CanPrimaryAttack() then return end  self:SetNextPrimaryFire(CurTime() + self:GetFireDelay())  self:EmitFireSound()   local altuse = self:GetDTInt(10)  if altuse == 0 then  self:TakeAmmo()  end  self:SetDTInt(10, (altuse - 1) % 3)   self:ShootBullets(self.Primary.Damage, self.Primary.NumShots, self:GetCone())  self.IdleAnimation = CurTime() + self:SequenceDuration()  end   wept.GetDisplayAmmo = function(self, clip, spare, maxclip)  local minus = self:GetDTInt(10)  return math.max(0, (clip * 3) + minus) - 2, spare * 3, maxclip * 3  end   wept.ConeMax = 3  wept.ConeMin = 1.5 end).CollectiveName = "Salvo"  local branch = GAMEMODE:AddNewRemantleBranch(SWEP, 2, "'Icarus' Rocket Launcher", "Smaller radius and increased taper but increased base damage, clip, and knockback", function(wept)  wept.Primary.Damage = wept.Primary.Damage * 1.85  wept.Primary.ClipSize = wept.Primary.ClipSize + 1  wept.Taper = 0.5  wept.Radius = wept.Radius * (58/78)   wept.EntModify = function(self, ent)  ent:SetDTInt(0, 2)   local owner = self:GetOwner()  owner.RemoteDetRocket = ent  owner:SetGroundEntity(NULL)  owner:SetVelocity(-260 * owner:GetAimVector())   self:SetNextSecondaryFire(CurTime() + 0.2)  end   wept.ViewModelBoneMods = {  ["base"] = { scale = Vector(1, 1, 1), pos = Vector(-10, 16, -6), angle = Angle(0, 0, 0) },  }  wept.ViewModelBoneModsNoCentre = {  ["base"] = { scale = Vector(1, 1, 1), pos = Vector(-10, 3, -6), angle = Angle(0, 0, 0) },  }   wept.HUD3DBone = "base"  wept.HUD3DPos = Vector(5, 0, 20)  wept.FireAnimSpeed = 1  wept.WalkSpeed = SPEED_SLOW   wept.VElements = {  ["pipe"] = { type = "Model", model = "models/props_animated_breakable/smokestack.mdl", bone = "ValveBiped.Bip01_Spine4", rel = "back1", pos = Vector(24.489, 0, 0), angle = Angle(90.647, 0, 0), size = Vector(0.019, 0.021, 0.009), color = Color(165, 165, 165, 255), surpresslightning = false, material = "phoenix_storms/trains/track_beamside", skin = 0, bodygroup = {} },  ["end"] = { type