), size = { x = 10, y = 10 }, color = Color(255, 0, 0, 255), nocull = true, additive = true, vertexalpha = true, vertexcolor = true, ignorez = false},  ["glow+"] = { type = "Sprite", sprite = "effects/redflare", bone = "ValveBiped.Bip01_R_Hand", rel = "skull", pos = Vector(3.253, 1.531, 0.949), size = { x = 9.995, y = 9.995 }, color = Color(255, 255, 72, 255), nocull = true, additive = true, vertexalpha = true, vertexcolor = true, ignorez = false},  ["thing++"] = { type = "Model", model = "models/props_c17/grinderclamp01a.mdl", bone = "ValveBiped.Bip01_R_Forearm", rel = "", pos = Vector(10.243, 0.282, -2.034), angle = Angle(-179.083, -93.17, -93.789), size = Vector(0.404, 0.404, 0.404), color = Color(255, 255, 255, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} }, }  SWEP.WElementPurge = {  ["base"] = true }  SWEP.StaminaUsage = 0  function SWEP:OnMeleeHit(hitent, hitflesh, tr)  if hitent:IsPlayer() then  local owner = self:GetOwner()  if owner:Health() < owner:GetMaxHealth() then  owner:SetHealth(math.min(owner:Health() + self.MeleeDamage/3, owner:GetMaxHealth()))              if SERVER then                 hitent:AddWounds(math.ceil(self.MeleeDamage/2))             end  end  end end  function SWEP:SetNextAttack()  local owner = self:GetOwner()  local armdelay = owner:GetMeleeSpeedMul()  self:SetNextPrimaryFire(CurTime() + self.Primary.Delay * armdelay) end  function SWEP:PlaySwingSound()  self:EmitSound("weapons/knife/knife_slash"..math.random(2)..".wav", 72, math.Rand(85, 95)) end  function SWEP:PlayHitSound()  self:EmitSound("weapons/knife/knife_hitwall1.wav", 72, math.Rand(55, 65))  self:EmitSound("ambient/machines/slicer"..math.random(4)..".wav", 75, math.random(230, 236), 0.9, CHAN_WEAPON + 1) end  function SWEP:PlayHitFleshSound()  self:EmitSound("physics/flesh/flesh_squishy_impact_hard"..math.random(4)..".wav")  self:EmitSound("physics/body/body_medium_break"..math.random(2, 4)..".wav") end  function SWEP:PostOnMeleeHit(hitent, hitflesh, tr)  if hitent:IsValid() and hitent:IsPlayer() and hitent:Health() <= 0 then  for i = 1, 3 do  local effectdata = EffectData()  effectdata:SetOrigin(tr.HitPos)  effectdata:SetMagnitude(1500)  effectdata:SetNormal(Vector(0, 0, 1))  effectdata:SetEntity(hitent)  util.Effect("headshot", effectdata, true, true)  end   hitent:Dismember(table.Random({  DISMEMBER_HEAD, DISMEMBER_LEFTARM, DISMEMBER_LEFTLEG, DISMEMBER_RIGHTARM, DISMEMBER_RIGHTLEG  }))  end end