AddCSLuaFile()  SWEP.Base = "weapon_zs_zombie" local BaseClass = baseclass.Get("weapon_zs_zombie")  SWEP.MeleeDamage = 22 SWEP.SlowDownScale = 0  SWEP.AlertDelay = 3.5  SWEP.PassiveMoan = false  function SWEP:MeleeHitEntity(ent, trace, damage, forcescale)  BaseClass.MeleeHitEntity(self, ent, trace, damage, forcescale)   if ent:IsValid() then  if SERVER then  util.PoisonBlastDamage(self, self:GetOwner(), self:GetOwner():GetShootPos(), 65, 28, true, true)  end   local effectdata = EffectData()  effectdata:SetOrigin(trace.HitPos)  effectdata:SetMagnitude(0.5)  util.Effect("explosion_chem", effectdata, true)  end end  function SWEP:Reload()  self:SecondaryAttack() end  function SWEP:PlayAlertSound()  self:GetOwner():EmitSound("npc/combine_gunship/gunship_moan.wav", 75, math.random(70,75)) end SWEP.PlayIdleSound = SWEP.PlayAlertSound  function SWEP:PlayAttackSound()  self:EmitSound("npc/antlion_guard/angry"..math.random(3)..".wav", 75, math.random(115,120))  self:EmitSound("npc/fast_zombie/fz_alert_close1.wav", 75, math.Rand(50, 60), 0.5, CHAN_WEAPON + 1) end  local matSkin = Material("models/zombie_classic/zombie_classic_sheet")  function SWEP:PreDrawViewModel(vm)  render.ModelMaterialOverride(matSkin)  render.SetColorModulation(29/255, 146/255, 0.12) end  function SWEP:PostDrawViewModel(vm)  render.ModelMaterialOverride()  render.SetColorModulation(1, 1, 1) end