AddCSLuaFile()  SWEP.Base = "weapon_zs_zombie" local BaseClass = baseclass.Get("weapon_zs_zombie")  SWEP.PrintName = "Chem Juggernaut"  SWEP.Primary.Delay = 1.62  SWEP.MeleeDamage = 25 SWEP.SlowDownScale = 0  SWEP.AlertDelay = 3.5  SWEP.PassiveMoan = false  function SWEP:MeleeHitEntity(ent, trace, damage, forcescale)  BaseClass.MeleeHitEntity(self, ent, trace, damage, forcescale)   if ent:IsValid() then  if SERVER then  local owner = self:GetOwner()   util.PoisonBlastDamage(self, owner, owner:GetShootPos(), 65, 35, true, true)   local status = ent:GetStatus("chemjuggernaut")  if status and status:IsValid() then  status.Applier = owner  status:SetDie(4)  else  status = ent:GiveStatus("chemjuggernaut", 4)  status.Applier = owner  end  end   local effectdata = EffectData()  effectdata:SetOrigin(trace.HitPos)  effectdata:SetMagnitude(0.5)  util.Effect("explosion_chem", effectdata, true)  end end  function SWEP:Reload()  self:SecondaryAttack() end  function SWEP:PlayAlertSound()  self:GetOwner():EmitSound("npc/combine_soldier/die"..math.random(3)..".wav", 75, math.random(40, 45)) end SWEP.PlayIdleSound = SWEP.PlayAlertSound  function SWEP:PlayAttackSound()  self:EmitSound("npc/metropolice/pain"..math.random(3)..".wav", 70, math.random(45, 50), .75)  self:EmitSound("npc/zombie_poison/pz_warn"..math.random(2)..".wav", 70, math.random(60, 65), 0.65, CHAN_WEAPON + 1) end  local matSkin = Material("zombiesurvival/zombie/combinesoldiersheet_chem")  function SWEP:PreDrawViewModel(vm)  render.ModelMaterialOverride(matSkin)  render.SetColorModulation(0.45, 0.65, 0.45) end  function SWEP:PostDrawViewModel(vm)  render.ModelMaterialOverride()  render.SetColorModulation(1, 1, 1) end