AddCSLuaFile() local BaseClass = baseclass.Get("weapon_zs_redmarrow")  if CLIENT then  SWEP.PrintName = "Cold Marrow" end  SWEP.Base = "weapon_zs_redmarrow"  SWEP.MeleeDamage = 22  SWEP.Secondary.Delay = 5  function SWEP:ApplyMeleeDamage(ent, trace, damage)  if SERVER and ent:IsPlayer() then  local owner = self:GetOwner()  local gt = ent:GiveStatus("debuff_frost", 8, owner)   if gt and gt:IsValid() then  gt.Applier = owner  end  ent:AddLegDamageExt(12, owner, self, SLOWTYPE_COLD)  end   BaseClass.ApplyMeleeDamage(self, ent, trace, damage) end  local function DoFleshThrow(pl, wep)  if pl:IsValid() and pl:Alive() and wep:IsValid() then  pl:ResetSpeed()  pl.LastRangedAttack = CurTime()   if SERVER then  local startpos = pl:GetShootPos()  local aimang = pl:EyeAngles()  local ang = Angle(aimang.p, aimang.y, aimang.r)              local ent = ents.Create("projectile_coldmarrow")             if ent:IsValid() then                 ent:SetPos(startpos)                 ent:SetAngles(ang)                 ent:SetOwner(pl)                 ent:Spawn()                  local phys = ent:GetPhysicsObject()                 if phys:IsValid() then                     phys:SetVelocityInstantaneous(ang:Forward() * 120)                 end             end   pl:EmitSound("physics/glass/glass_impact_bullet"..math.random(4)..".wav", 72, math.Rand(85, 95))  end  end end  function SWEP:SecondaryAttack()  local owner = self:GetOwner()  if CurTime() < self:GetNextPrimaryFire() or CurTime() < self:GetNextSecondaryFire() or IsValid(owner.FeignDeath) then return end   self:SetNextSecondaryFire(CurTime() + self.Secondary.Delay)  self:SetNextPrimaryFire(CurTime() + self.Primary.Delay)   self:GetOwner():DoZombieEvent()  self:EmitSound("npc/fast_zombie/leap1.wav", 74, math.Rand(110, 130))  self:EmitSound(string.format("physics/body/body_medium_break%d.wav", math.random(2, 4)), 72, math.Rand(85, 95))  self:GetOwner():RawCapLegDamage(CurTime() + 2)  self:SendWeaponAnim(ACT_VM_HITCENTER)  self.IdleAnimation = CurTime() + self:SequenceDuration()   timer.Simple(0.9, function() DoFleshThrow(owner, self) end) end  function SWEP:ViewModelDrawn()  render.ModelMaterialOverride(0)  render.SetColorModulation(1, 1, 1) end  local matSheet = Material("Models/flesh") function SWEP:PreDrawViewModel(vm)  render.ModelMaterialOverride(matSheet)  render.SetColorModulation(0.1, 0.5, 0.9) end 