AddCSLuaFile()  SWEP.PrintName = "Crowbar" SWEP.Description = "An effective and fast swinging melee weapon, the crowbar also has the ability to instantly kill headcrabs."  if CLIENT then  SWEP.ViewModelFOV = 65 end  SWEP.Base = "weapon_zs_basemelee"  SWEP.ViewModel = "models/weapons/c_crowbar.mdl" SWEP.WorldModel = "models/weapons/w_crowbar.mdl" SWEP.UseHands = true  SWEP.HoldType = "melee"  SWEP.DamageType = DMG_CLUB  SWEP.MeleeDamage = 38 SWEP.OriginalMeleeDamage = SWEP.MeleeDamage SWEP.MeleeRange = 58 SWEP.MeleeSize = 1.5 SWEP.MeleeKnockBack = 110  SWEP.Primary.Delay = 0.7  SWEP.SwingTime = 0.4 SWEP.SwingRotation = Angle(30, -30, -30) SWEP.SwingHoldType = "grenade"  SWEP.BlockRotation = Angle(0, 40, -60) SWEP.BlockOffset = Vector(-10, 7, 5)  SWEP.Stability = 50 SWEP.BlockReduction = 0.7  SWEP.AllowQualityWeapons = true  SWEP.OverkillFactor = 0.5  SWEP.TreatDamageTypeForRounds = DMG_DIRECT  SWEP.RespectHitGroup = true SWEP.HeadshotMulti = 1.5  SWEP.StaminaUsage = 0.16  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_MELEE_RANGE, 3)  function SWEP:PlaySwingSound()  self:EmitSound("weapons/iceaxe/iceaxe_swing1.wav", 75, 100, 0.55) end  function SWEP:PlayHitSound()  self:EmitSound(string.format("weapons/crowbar/crowbar_impact%d.wav", math.random(1,2)), 90, math.random(90, 120), 0.7) end  function SWEP:PlayHitFleshSound()  self:EmitSound(string.format("physics/flesh/flesh_impact_bullet%d.wav", math.random(3,5))) end  function SWEP:PostOnMeleeHit(hitent, hitflesh, tr)  if hitent:IsValid() and hitent:IsPlayer() and hitent:Team() == TEAM_UNDEAD and hitent:IsHeadcrab() and gamemode.Call("PlayerShouldTakeDamage", hitent, self:GetOwner()) then  hitent:TakeSpecialDamage(hitent:Health(), DMG_DIRECT, self:GetOwner(), self, tr.HitPos)  end end  function SWEP:BeforeSwing(dmgmul)  self.RespectHitGroup = self:IsHeavy()   return dmgmul * (self:IsHeavy() and 0.85 or 1) end 