_R_Hand", rel = "1", pos = Vector(0, 1.776, -8.063), angle = Angle(90, 0, 0), size = Vector(0.3, 0.3, 0.3), color = Color(139, 0, 0, 255), surpresslightning = false, material = "phoenix_storms/metal_plate", skin = 0, bodygroup = {} },         ["3"] = { type = "Model", model = "models/hunter/tubes/circle2x2c.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "1", pos = Vector(0, -0.021, -1.714), angle = Angle(0, -90, 0), size = Vector(0.023, 0.013, 1.488), color = Color(65, 65, 65, 255), surpresslightning = false, material = "phoenix_storms/metal_plate", skin = 0, bodygroup = {} },         ["raczka"] = { type = "Model", model = "models/weapons/w_pist_fiveseven.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "1", pos = Vector(0, 6.006, 0.238), angle = Angle(90, -90, 0), size = Vector(0.921, 0.921, 0.921), color = Color(65, 65, 65, 255), surpresslightning = false, material = "phoenix_storms/metal_plate", skin = 0, bodygroup = {} },         ["railbase"] = { type = "Model", model = "models/props_c17/signpole001.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "1", pos = Vector(0, -0.57, 4.3), angle = Angle(0, 0, 0), size = Vector(0.008, 0.008, 0.008), color = Color(220, 220, 220, 255), surpresslightning = false, material = "", skin = 0, bodygroup = {} },         ["2+++++"] = { type = "Model", model = "models/hunter/tubes/tube1x1x2.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "1", pos = Vector(0, 0, 7.171), angle = Angle(0, 0, 0), size = Vector(0.046, 0.046, 0.136), color = Color(139, 0, 0, 255), surpresslightning = false, material = "shadertest/texturescroll", skin = 0, bodygroup = {} },         ["front_2"] = { type = "Model", model = "models/xeon133/offroad/off-road-30.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "2+++++++++", pos = Vector(0, 0, 6.609), angle = Angle(90, 0, 0), size = Vector(0.986, 0.068, 0.068), color = Color(255, 37, 36, 255), surpresslightning = false, material = "models/props_combine/metal_combinebridge001", skin = 0, bodygroup = {} },         ["2+"] = { type = "Model", model = "models/props_phx/misc/potato_launcher.mdl", bone = "ValveBiped.Bip01_R_Hand", rel = "1", pos = Vector(0, 0, 28.982), angle = Angle(180, 0, 0), size = Vector(0.137, 0.137, 0.137), color = Color(65, 65, 65, 255), surpresslightning = false, material = "phoenix_storms/metal_plate", skin = 0, bodygroup = {} }     } end  SWEP.ShowViewModel = false SWEP.ShowWorldModel = false  SWEP.Base = "weapon_zs_base" local BaseClass = baseclass.Get("weapon_zs_base")  SWEP.HoldType = "ar2"  SWEP.ViewModel = "models/weapons/cstrike/c_snip_sg550.mdl" SWEP.WorldModel = "models/weapons/w_pist_fiveseven.mdl" SWEP.UseHands = true  SWEP.ReloadSound = Sound("weapons/awp/awp_clipout.wav") SWEP.Primary.Sound = Sound(")weapons/awp/awp1.wav") SWEP.Primary.Damage = 158 SWEP.Primary.NumShots = 1 SWEP.Primary.Delay = 0.6  SWEP.Primary.ClipSize = 24 SWEP.Primary.Automatic = false SWEP.Primary.Ammo = "357" SWEP.Primary.DefaultClip = 15  SWEP.RequiredClip = 2  SWEP.Primary.Gesture = ACT_HL2MP_GESTURE_RANGE_ATTACK_CROSSBOW SWEP.ReloadGesture = ACT_HL2MP_GESTURE_RELOAD_SHOTGUN  SWEP.ConeMax = 5.75 SWEP.ConeMin = 0  SWEP.IronSightsPos = Vector(5.015, -8, 2.52) SWEP.IronSightsAng = Vector(0, 0, 0)  SWEP.WalkSpeed = SPEED_SLOWER  SWEP.Tier = 6 SWEP.MaxStock = 2  SWEP.FireModes = 2 SWEP.FireModeNames = {"Full-Zoom", "Half-Zoom"} SWEP.FireModeNames3D = {"FULL", "HALF"}  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_RELOAD_SPEED, 0.09, 1)  function SWEP:EmitFireSound()  self:EmitSound("weapons/zs_dds/zs_gc.ogg", 85, 235, 0.4, CHAN_AUTO)  self:EmitSound("weapons/zs_dds/zs_deus.ogg", 85, 100, 0.65)  self:EmitSound("weapons/awp/awp1.wav", 80, 210, 0.6, CHAN_WEAPON + 20) end  function SWEP:IsScoped()  return self:GetIronsights() and self.fIronTime and self.fIronTime + 0.25 <= CurTime() end  function SWEP:OnZombieKilled(zombie)  local killer = self:GetOwner()   if killer:IsValid() and zombie:WasHitInHead() and self.HitPos then  local new_zombie  for _,v in pairs(team.GetPlayers(TEAM_ZOMBIE)) do  if v ~= zombie and v:GetPos():DistToSqr(self.HitPos) <= 200000 then  new_zombie = v  break  end  end   if not new_zombie then return end    local boneid = new_zombie:GetHitBoxBone(HITGROUP_HEAD, 0)  if boneid and boneid > 0 then  local bp = new_zombie:GetBonePositionMatrixed(boneid)  if bp then  bp.z = bp.z + 12  local dir = (bp - self.HitPos):GetNormalized()   timer.Simple(0.05, function()  killer:FireBulletsLua(self.HitPos, dir, 0, 1, self.Primary.Damage, nil, nil, "tracer_rico", self.BulletCallback, nil, nil, nil, nil, self)  end)  end  end   self.Headshots = (self.Headshots or 0) + 1   if self.Headshots >= 5 and self:GetShotsLeft() == 0 then  self.Headshots = 0  self:SetShotsLeft(3)  end  end end  function SWEP:Think()  BaseClass.Think(self)   local shotsleft = self:GetShotsLeft()  if shotsleft > 0 and CurTime() >= self:GetNextShot() then  self:SetShotsLeft(shotsleft - 1)  self:SetNextShot(CurTime() + self:GetFireDelay(4))   if self:Clip1() > 0 and self:GetReloadFinish() == 0 then  self:EmitFireSound()  self:ShootBullets(self.Primary.Damage, self.Primary.NumShots, self:GetCone())   self.IdleAnimation = CurTime() + self:SequenceDuration()  else  self:SetShotsLeft(0)  end  end end  function SWEP:SetNextShot(nextshot)  self:SetDTFloat(5, nextshot) end  function SWEP:GetNextShot()  return self:GetDTFloat(5) end  function SWEP:SetShotsLeft(shotsleft)  self:SetDTInt(1, shotsleft) end  function SWEP:GetShotsLeft()  return self:GetDTInt(1) end  function SWEP.BulletCallback(attacker, tr, dmginfo)  local effectdata = EffectData()  effectdata:SetOrigin(tr.HitPos)  effectdata:SetNormal(tr.HitNormal)  util.Effect("hit_hunter", effectdata)   local inflictor = dmginfo:GetInflictor()   inflictor.HitPos = tr.HitPos end  function SWEP:FireModeChanged(fm)     fm = tonumber(fm)     self.IronsightsMultiplier = fm == 1 and 0.55 or 0.25 end  function SWEP:PlayFireModeSwitchSound(fm)     fm = tonumber(fm)     if CLIENT then         if fm == 1 then             surface.PlaySound("weapons/zoom/zoom_soundtest.wav")         else             surface.PlaySound("weapons/zoom/zoom_soundtest2.wav")         end     end end  if CLIENT then  SWEP.IronsightsMultiplier = 0.25   function SWEP:GetViewModelPosition(pos, ang)  if GAMEMODE.DisableScopes then return end   if self:IsScoped() then  return pos + ang:Up() * 256, ang  end   return self.BaseClass.GetViewModelPosition(self, pos, ang)  end   function SWEP:DrawHUDBackground()  if GAMEMODE.DisableScopes then return end   if self:IsScoped() then  self:DrawRegularScope()  end  end end 