Emit = 0  SWEP.FlameRadius = 85  SWEP.StaminaUsage = 0.23  AccessorFuncDT(SWEP, "CircleOfPurity", "Bool", 10) AccessorFuncDT(SWEP, "NextModeToggle", "Float", 10) AccessorFuncDT(SWEP, "NextFlameTick", "Float", 11) AccessorFuncDT(SWEP, "NextShieldCharge", "Float", 12)  function SWEP:Reload()     if CurTime() <= self:GetNextModeToggle() then return end      self:TogglePurity() end  function SWEP:GetWalkSpeed()     if self:GetNextShieldCharge() ~= 0 and CurTime() < self:GetNextShieldCharge() then         return self.WalkSpeed * 4     end      BaseClass.GetWalkSpeed(self) end  function SWEP:TogglePurity()     self:SetCircleOfPurity(not self:GetCircleOfPurity())     self:EmitSound("weapons/physcannon/energy_disintegrate5.wav", 65, 190)      self:SetNextModeToggle(CurTime() + 2)      if CLIENT then         local owner = self:GetOwner()      local pos = owner:GetPos()         pos.z = pos.z + 10          local particle         local emitter = ParticleEmitter(pos)         emitter:SetNearClip(12, 16)          local radius = self.FlameRadius          for i=1, 145 do             local angler = AngleRand()             local dist = math.Rand(radius*0.9, radius)             particle = emitter:Add("effects/fire_embers"..math.random(1, 3), pos + Vector(math.cos(angler.y) * dist, math.sin(angler.y) * dist, 0))             particle:SetColor(255, 255, 1)             particle:SetDieTime(0.1)             particle:SetStartAlpha(255)             particle:SetEndAlpha(0)             particle:SetStartSize(0)             particle:SetEndSize(math.Rand(31, 56))             particle:SetRoll(math.Rand(0, 360))             particle:SetStartLength(0)             particle:SetEndLength(49)             particle:SetVelocity(angler:Forward() * 100)             particle:SetCollide(true)         end          emitter:Finish() emitter = nil collectgarbage("step", 64)     end end  function SWEP:Bash(tr, hitent)     if CurTime() < self:GetBashEnd() or self:GetNextShieldCharge() ~= 0 then return end      BaseClass.Bash(self)      if CurTime() > self:GetBashEnd() then return end      local owner = self:GetOwner()      if IsFirstTimePredicted() then         owner:EmitSound("npc/env_headcrabcanister/launch.wav", 65, 230)     end      self.ChargePosition = self:GetPos()      self:SetNextShieldCharge(CurTime() + 0.35)     owner:ResetSpeed() end  function SWEP:Think()     local owner = self:GetOwner()     if self:GetCircleOfPurity() then         if CLIENT then             local time = CurTime()             local pos = owner:GetPos()             pos.z = pos.z + 10              if time < self.NextEmit then return end             self.NextEmit = time + 0.05              local particle             local emitter = ParticleEmitter(pos)             emitter:SetNearClip(12, 16)              local vec = Vector(0, 0, 100)              local radius = self.FlameRadius              for i=1, 45 do                 local angler = AngleRand()                 local dist = math.Rand(radius*0.9, radius)                 particle = emitter:Add("effects/fire_embers"..math.random(1, 3), pos + Vector(math.cos(angler.y) * dist, math.sin(angler.y) * dist, 0))                 particle:SetColor(255, 255, 100)                 particle:SetDieTime(0.1)                 particle:SetStartAlpha(255)                 particle:SetEndAlpha(0)                 particle:SetStartSize(0)                 particle:SetEndSize(math.Rand(31, 56))                 particle:SetRoll(math.Rand(0, 360))                 particle:SetStartLength(0)                 particle:SetEndLength(19)                 particle:SetVelocity(vec)                 particle:SetCollide(true)             end              emitter:Finish() emitter = nil collectgarbage("step", 64)         end          if CurTime() > self:GetNextFlameTick() then             local multi = (owner.FireDamageTakenMul or 1) * (owner.SelfDamageMul or 1)             local start_val = 4 * multi              local consume_blood = math.ceil(math.min(start_val, owner:GetBloodArmor()))             local consume_health = math.ceil(math.min(start_val - consume_blood, owner:Health() - 1))             if SERVER then                 owner:SetBloodArmor(owner:GetBloodArmor() - consume_blood)                 owner:SetHealth(owner:Health() - consume_health)                  local pos = self:GetPos()                  local c = 0                  self.AmmoTrinketOverride = 0.33                  for _, ent in pairs(ents.FindInSphere(pos, self.FlameRadius)) do                     if c >= 8 then break end                                          if ent and (ent:IsValidLivingZombie() and WorldVisible(pos, ent:NearestPoint(pos))) then                         c = c + 1                          owner:WrappedFlagDamageFunction({NoBloodArmor = true}, function()                             ent:TakeSpecialDamage(self.MeleeDamage * 0.15, DMG_BURN, owner, self)                         end)                     end                 end                  self.AmmoTrinketOverride = 1             end              if consume_blood + consume_health <= 0.2 then                 self:TogglePurity()             end              self:SetNextFlameTick(CurTime() + 0.33)         end     end      if self:GetNextShieldCharge() ~= 0 then         if CLIENT then             local time = CurTime()             local pos = owner:WorldSpaceCenter()              if time < self.NextEmit then return end             self.NextEmit = time + 0.1              local particle             local emitter = ParticleEmitter(pos)             emitter:SetNearClip(12, 16)              local vec = owner:GetVelocity() * 0.5              for i=1, 15 do                 particle = emitter:Add("effects/fire_embers"..math.random(1, 3), pos + VectorRand() * 20)                 particle:SetColor(255, 70, 70)                 particle:SetDieTime(0.3)                 particle:SetStartAlpha(255)                 particle:SetEndAlpha(0)                 particle:SetStartSize(10)                 particle:SetEndSize(0)                 particle:SetRoll(math.Rand(0, 360))                 particle:SetVelocity(vec)                 particle:SetCollide(true)             end              emitter:Finish() emitter = nil collectgarbage("step", 64)         end          if CurTime() > self:GetNextShieldCharge() then             self:SetNextShieldCharge(0)             owner:ResetSpeed()              if SERVER then                 local pos = owner:WorldSpaceCenter()                 local dist = self.ChargePosition:DistToSqr(pos)                  if dist >= 8100 then                     local dist_multi = math.Clamp(1 - ((22000 - dist)/22000), 0, 1)                      util.BlastDamageEx(self, owner, pos, 110, self.MeleeDamage * 1.55 * dist_multi, DMG_ALWAYSGIB, 0.8, {[owner] = true})                 end             end         end     end      BaseClass.Think(self) end  function SWEP:PlaySwingSound()  self:EmitSound("weapons/iceaxe/iceaxe_swing1.wav", 75, math.random(45, 55)) end  function SWEP:PlayHitSound()  self:EmitSound("physics/metal/metal_canister_impact_hard"..math.random(3)..".wav", 75, math.Rand(80, 85)) end  function SWEP:PlayHitFleshSound()  self:EmitSound("physics/body/body_medium_break"..math.random(2, 4)..".wav", 75, math.Rand(86, 90), nil, CHAN_AUTO) end   if not CLIENT then return end