(0, 0, 0), angle = Angle(0, -90, 0) },         ["ValveBiped.Bip01_R_Forearm"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(12.222, 0, -10) },         ["v_weapon.elite_left"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 90, 0) },         ["ValveBiped.Bip01_L_Hand"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, -25.556, 96.666) },         ["ValveBiped.Bip01_R_Hand"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, -30, -96.667) }     }      SWEP.ViewModelBoneModsPri = {         ["ValveBiped.Bip01_R_Forearm"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) },         ["ValveBiped.Bip01_L_UpperArm"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) },         ["v_weapon.elite_right"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) },         ["v_weapon.elite_left"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) },         ["ValveBiped.Bip01_L_Forearm"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) },         ["ValveBiped.Bip01_L_Hand"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) },         ["ValveBiped.Bip01_R_UpperArm"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) },         ["ValveBiped.Bip01_R_Hand"] = { scale = Vector(1, 1, 1), pos = Vector(0, 0, 0), angle = Angle(0, 0, 0) }     }      SWEP.ViewModelBoneModsDesired = SWEP.ViewModelBoneModsPri     SWEP.ViewModelBoneMods = SWEP.ViewModelBoneModsPri end  SWEP.Base = "weapon_zs_base"  SWEP.HoldType = "duel"  SWEP.ViewModel = "models/weapons/cstrike/c_pist_elite.mdl" SWEP.WorldModel = "models/weapons/w_pist_elite.mdl" SWEP.UseHands = true  SWEP.Primary.Damage = 70 SWEP.Primary.NumShots = 1  SWEP.Primary.Delay = 0.3 SWEP.Primary.DelayPri = 0.3 SWEP.Primary.DelaySec = 0.2  SWEP.DrawSound = "weapons/zs_dds/zs_cock.wav"  SWEP.Primary.ClipSize = 20 SWEP.Primary.Automatic = true SWEP.Primary.Ammo = "pistol" SWEP.Primary.DefaultClip = 150  SWEP.Secondary.Automatic = true  SWEP.ConeMax = 2.5 SWEP.ConeMin = 1.5  SWEP.ConeMaxPri = 2.5 SWEP.ConeMinPri = 1.5  SWEP.ConeMaxSec = 4.95 SWEP.ConeMinSec = 3.55  SWEP.ReloadSpeed = 1.2  SWEP.FireAnimSpeed = 1.5  SWEP.Tier = 6 SWEP.MaxStock = 2  SWEP.ReloadSpeed = 1.6  SWEP.Pens = 1 SWEP.PenTaper = 0.25 SWEP.PenHeadOnly = true  local markate_index = 15 local markate_float = 15 local markate_bool  = 15  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_MAX_SPREAD, -0.2, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_RELOAD_SPEED, 0.1, 1)  SWEP.WeaponBuildup = {  Colour = {200, 70, 70, 255},  Name = "Hits" }  function SWEP:EmitFireSound()     self:EmitSound("weapons/zs_dds/zs_duals.ogg", 75, math.random(98, 103), 1, CHAN_WEAPON + 20)     self:EmitSound("weapons/zs_dds/zs_dd.ogg", 75, math.random(98, 103), 0.5, CHAN_WEAPON + 21)     self:SetDTBool(1, not self:GetDTBool(1)) end  function SWEP:EmitReloadSound()     if IsFirstTimePredicted() then         self:EmitSound("weapons/zs_dds/zs_enfreload.wav", 75, math.random(98, 103))     end      self.ViewModelBoneModsDesired = self.ViewModelBoneModsPri end  function SWEP:SendReloadAnimation()     self:SendWeaponAnim(ACT_VM_RELOAD)      local anim_duration = self:SequenceDuration()     local reload_speed = self.ReloadSpeed * self:GetReloadSpeedMultiplier()      timer.Simple((anim_duration * 0.8) / reload_speed, function()         if not IsValid(self) then return end          self:SendWeaponAnim(ACT_VM_DRAW)         self:EmitSound("weapons/zs_dds/zs_cock.wav", 75, math.random(110, 120))     end) end  function SWEP:ProcessReloadEndTime()     local reloadspeed = self.ReloadSpeed * self:GetReloadSpeedMultiplier()      self:SetReloadFinish(CurTime() + self:SequenceDuration() / reloadspeed)     if not self.DontScaleReloadSpeed then         self:GetOwner():GetViewModel():SetPlaybackRate(reloadspeed / 2.25)     end end  function SWEP:PrimaryAttack()     self.ConeMax = self.ConeMaxPri     self.ConeMin = self.ConeMinPri     self.Primary.Delay = self.Primary.DelayPri     self.ViewModelBoneModsDesired = self.ViewModelBoneModsPri     self.FireAnimSpeed = 1.5      self:SetDTBool(markate_bool, false)      BaseClass.PrimaryAttack(self) end  function SWEP:SecondaryAttack()     if not self:CanPrimaryAttack() then         return     end      self.ConeMax = self.ConeMaxSec     self.ConeMin = self.ConeMinSec     self.Primary.Delay = self.Primary.DelaySec     self.ViewModelBoneModsDesired = self.ViewModelBoneModsSec     self.FireAnimSpeed = 2.25      self:SetDTBool(markate_bool, true)      BaseClass.PrimaryAttack(self) end  function SWEP:SendWeaponAnimation()     if IsFirstTimePredicted() then         self:SendWeaponAnim(self:GetDTBool(1) and ACT_VM_PRIMARYATTACK or ACT_VM_SECONDARYATTACK)         self:GetOwner():GetViewModel():SetPlaybackRate(self.FireAnimSpeed)         self.IdleAnimation = CurTime() + self:SequenceDuration()     end end  function SWEP:Deploy()     self:SetWeaponBuildup(0)     return BaseClass.Deploy(self) end  function SWEP:Markate()     local owner     = self:GetOwner()     local shoot_pos = owner:GetShootPos()      owner:EmitSound("npc/scanner/combat_scan3.wav", 75, 50)      local tgts = {}     for _, v in pairs(team.GetPlayers(TEAM_ZOMBIE)) do         if #tgts >= 8 then break end          local eye_pos = v:EyePos()         local dist = shoot_pos:DistToSqr(eye_pos)          if dist <= 160000 then             tgts[#tgts + 1] = v         end     end      for i, v in ipairs(tgts) do         self:SetDTEntity(markate_index + i, v)     end      self:SetDTFloat(markate_float, CurTime() + 0.2) end  function SWEP:ShootBullets(dmg, numbul, cone)     local owner = self:GetOwner()     self:SendWeaponAnimation()     owner:DoAttackEvent()      local owner_pos = owner:GetShootPos()     local vector = owner:GetAimVector()     local markate_mode = self:GetDTBool(markate_bool)     local dmg_multi = 1      if markate_mode then         for i = markate_index, markate_index + 8 do             local markated = self:GetDTEntity(i)             local rem      = false              if markated:IsValidLivingZombie() then                 rem = true                  local boneid = markated:GetHitBoxBone(HITGROUP_HEAD, 0)                 if boneid and boneid > 0 then                     local bp = markated:GetBonePositionMatrixed(boneid)                     if bp then                         bp.z = bp.z + 12                         vector = (bp - owner_pos):GetNormalized()                     end                 end             end              if rem then                 self:SetDTEntity(i, NULL)                 dmg_multi = 1.5                 break             end         end     end      DO_BULLET_LAG_COMP = true     owner:FireBulletsLua(owner_pos, vector, cone, numbul, dmg * dmg_multi, nil, self.Primary.KnockbackScale, self.TracerName, self.BulletCallback, self.Primary.HullSize, nil, self.Primary.MaxDistance, nil, self)     DO_BULLET_LAG_COMP = nil end  function SWEP.BulletCallback(attacker, tr, dmginfo)     local ent = tr.Entity     if SERVER then         local wep = attacker:GetActiveWeapon()         if wep:GetDTBool(markate_bool) then return end          if ent:IsValidLivingZombie() then             if wep:GetWeaponBuildup() >= 1 then                 wep:SetWeaponBuildup(0)                 wep:Markate()             else                 wep:SetWeaponBuildup(math.min(1, wep:GetWeaponBuildup() + 0.11))             end         end     end end  if not CLIENT then return end  function SWEP:GetTracerOrigin()     local owner = self:GetOwner()     if owner:IsValid() then         local vm = owner:GetViewModel()         if vm and vm:IsValid() then             local attachment = vm:GetAttachment(self:Clip1() % 2 + 3)             if attachment then                 return attachment.Pos             end         end     end end  local ghostlerp = 0  function SWEP:CalcViewModelView(vm, oldpos, oldang, pos, ang)     if self:GetOwner():GetBarricadeGhosting() or self:GetReloadFinish() > 0 then         ghostlerp = math.min(1, ghostlerp + FrameTime() * 1)     elseif ghostlerp > 0 then         ghostlerp = math.max(0, ghostlerp - FrameTime() * 1.5)     end      if ghostlerp > 0 then         ang:RotateAroundAxis(ang:Right(), -20 * ghostlerp)     end      for key, value in pairs(self.ViewModelBoneModsDesired) do         local start = self.ViewModelBoneMods[key].angle          self.ViewModelBoneMods[key].angle = LerpAngle(0.05, start, value.angle)     end      return pos, ang end  local matTargetTri = Material("zombiesurvival/headshot_stacks.png") function SWEP:DrawHUD()     BaseClass.DrawHUD(self)      surface.SetMaterial(matTargetTri)      for i = markate_index, markate_index + 8 do         local markated = self:GetDTEntity(i)          if markated:IsValidLivingZombie() then             local tpos = markated:EyePos()             local to_screen = tpos:ToScreen()              if to_screen.visible then                 local x, y = to_screen.x, to_screen.y                 local mod_size = 32 + math.max(0, self:GetDTFloat(markate_float) - CurTime()) * 102                  surface.SetDrawColor(255, 100, 100, 55)                 surface.DrawTexturedRect(x - mod_size * 0.5, y - mod_size * 0.5, mod_size, mod_size)             end         end     end end 