itent = tr.Entity   if hitent:IsValidLivingZombie() and hitent:CooldownReady("Halberd") then                 dmginfo:ScaleDamage(1.5)  hitent:AddNewCooldown("Halberd", 7)  end  end  end   wept.EmitFireSound = function(self)  local wave = GAMEMODE:GetWave()   self:EmitSound(")weapons/usp/usp_unsil-1.wav", 75, 80 + (1 - (wave / 6)) * 30, 0.8, 21)  self:EmitSound(self.Primary.Sound, 75, 130 + (1 - (wave / 6)) * 70, 0.75, 22)  end   local mat = Material("phoenix_storms/future_vents")  wept.PreDrawViewModel = function(self, vm)  render.MaterialOverrideByIndex(0, mat)   BaseClass.PreDrawViewModel(self, vm)  end  wept.PostDrawViewModel = function(self, vm)  render.MaterialOverrideByIndex(0)   BaseClass.PostDrawViewModel(self, vm)  end     wept.OnZombieKilled = function() end end) branch.CollectiveName = "Cleanser" GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_FIRE_DELAY, -0.0304, 3) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_CLIP_SIZE, 1) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_RELOAD_SPEED, 0.1, 3)  branch = GAMEMODE:AddNewRemantleBranch(SWEP, 2, "'Waraxe' Tactical Pistol", "No damage scaling, fires 3 shots accurately, reduced clip size. Every 3 assists causes your next shot to inflict soften.", function(wept)  wept.Primary.Damage = wept.Primary.Damage * (13.25/27)  wept.Primary.ClipSize = 9  wept.Primary.NumShots = 3   wept.ViewModel = "models/weapons/cstrike/c_pist_glock18.mdl"  wept.WorldModel = "models/weapons/w_pist_glock18.mdl"   wept.ShowViewModel = false  wept.ShowWorldModel = false      wept.WeaponBuildup = {         Colour = {150, 150, 30, 255},         Name = "Assists"     }   if CLIENT then  wept.VElements = {  ["base2"] = { type = "Model", model = "models/props_citizen_tech/till001a_safetyarm01.mdl", bone = "v_weapon.Glock_Parent", rel = "base1", pos = Vector(2.289, 0, 5.222), angle = Angle(0, 90, 0), size = Vector(0.037, 0.046, 0.037), color = Color(255, 255, 255, 255), surpresslightning = false, mater