or(190, 210, 220), Color(140, 160, 170), Color(100, 145, 170), Color(70, 100, 150)} branch.Killicon = "weapon_zs_waraxe" branch.CollectiveName = "Waraxe" GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_FIRE_DELAY, -0.0304, 3) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_CLIP_SIZE, 1) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_HEADSHOT_MULTI, 0.07)  function SWEP:EmitFireSound()  self:EmitSound("weapons/fiveseven/fiveseven-1.wav", 75, 80 + (1 - (self:Clip1() / self.Primary.ClipSize)) * 30, 0.8, 21)  self:EmitSound(self.Primary.Sound, 75, 130 + (1 - (self:Clip1() / self.Primary.ClipSize)) * 70, 0.75, 22) end  function SWEP:ShootBullets(dmg, numbul, cone)  dmg = dmg + dmg * (self:Clip1() == 1 and 2 or (1 - self:Clip1() / self.Primary.ClipSize))   BaseClass.ShootBullets(self, dmg, numbul, cone) end  function SWEP:OnZombieKilled()  local killer = self:GetOwner()   if killer:IsValid() and self:Clip1() == 0 then  killer:GiveStack("renegade", 1 + self.MaxStacks)  end end