local BaseClass = baseclass.Get("weapon_zs_basemelee")  SWEP.PrintName = "Frotchet" SWEP.Description = "An axe made from frost. Charged attacks unleashes a powerful swing, creating an icy explosion when aimed at the ground. Slows zombie movement and attack speed."  SWEP.Base = "weapon_zs_basemelee"  SWEP.ViewModel = "models/weapons/c_sledgehammer/c_sledgehammer.mdl" SWEP.WorldModel = "models/weapons/w_crowbar.mdl" SWEP.UseHands = true  SWEP.HoldType = "melee2"  SWEP.MeleeDamage = 180 SWEP.MeleeRange = 75 SWEP.MeleeSize = 3 SWEP.MeleeKnockBack = 240  SWEP.Primary.Delay = 1.4 SWEP.Secondary.Delay = SWEP.Primary.Delay * 1.75  SWEP.WalkSpeed = SPEED_SLOWER  SWEP.HitAnim = ACT_VM_MISSCENTER  SWEP.SwingRotation = Angle(20, 0, -40) SWEP.SwingOffset = Vector(0, -30, 0) SWEP.SwingTime = 0.62 SWEP.SwingHoldType = "melee"  SWEP.SwingTimeSecondary = 0.85  SWEP.Tier = 5  SWEP.Stability = 80 SWEP.BlockReduction = 0.4  SWEP.BlockRotation = Angle(0, 20, -60) SWEP.BlockOffset = Vector(-5, 7, 10)  SWEP.AllowQualityWeapons = true  SWEP.StaminaUsage = 0.3  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.14) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_STAMINA_USAGE, -0.01, 1)  function SWEP:Initialize()  BaseClass.Initialize(self)   self.ChargeSound = CreateSound(self, "nox/scatterfrost.ogg") end  function SWEP:CanSecondaryAttack()  if self:GetOwner():IsHolding() or self:GetOwner():GetBarricadeGhosting() then return false end   return self:GetNextSecondaryFire() <= CurTime() and not self:IsSwinging() end  function SWEP:Think()  if self:IsHeavy() then  local charge = math.Clamp(1 - (self:GetSwingEnd() - CurTime()), 0, 1)  self.ChargeSound:PlayEx(1, math.min(255, 35 + charge * 100))  else  self.ChargeSound:Stop()  end   BaseClass.Think(self) end  function SWEP:PlaySwingSound()  self:EmitSound("nox/sword_miss.ogg", 75, math.random(40, 45)) end  function SWEP:PlayHitSound()  self:EmitSound("nox/frotchet_test1.ogg", 75, math.random(95, 105)) end  function SWEP:PlayHitFleshSound()  self:EmitSound("physics/flesh/flesh_bloody_break.wav", 80, math.random(95, 105)) end 