SWEP.PrintName = "Gunk Zombie Torso"  SWEP.Base = "weapon_zs_zombietorso"  SWEP.Secondary.Delay = 3.5 SWEP.NextPuke = 0 SWEP.PukeLeft = 0 AccessorFuncDT(SWEP, "Gunking", "Float", 1)  SWEP.PassiveMoan = false  function SWEP:ApplyMeleeDamage(ent, trace, damage)  if ent:IsPlayer() then  ent:GiveStatus("debuff_gunk", 5)  self.BaseClass.ApplyMeleeDamage(self, ent, trace, damage / 1.1)  else  self.BaseClass.ApplyMeleeDamage(self, ent, trace, damage)  end end  function SWEP:Reload()  self.BaseClass.SecondaryAttack(self) end  function SWEP:PlayAlertSound()  self:PlayAttackSound() end  function SWEP:PlayIdleSound()  self:GetOwner():EmitSound("npc/barnacle/barnacle_tongue_pull"..math.random(3)..".wav") end  function SWEP:PlayAttackSound()  if SERVER then  local owner = self:GetOwner()  local sndname = "npc/ichthyosaur/attack_growl"..math.random(3)..".wav"  for i = 1, 4 do  timer.Simple(0.04 * i,  function() if owner:IsValid() then owner:EmitSound(sndname, 75, 140 + i*8, 0.4, CHAN_AUTO) end  end)  end  end end  function SWEP:SecondaryAttack()  local owner = self:GetOwner()  if CurTime() < self:GetNextPrimaryFire() or CurTime() < self:GetNextSecondaryFire() or IsValid(owner.FeignDeath) then return end   self:SetNextSecondaryFire(CurTime() + self.Secondary.Delay)  self:SetNextPrimaryFire(CurTime() + self.Primary.Delay)   owner:DoReloadEvent()  self:EmitSound(string.format("npc/ichthyosaur/attack_growl%d.wav", math.random(3)), 72, math.random(132, 136))   if SERVER then  self:SetGunking(CurTime())  timer.Simple(0.4, function()    if self:IsValid() then  self.PukeLeft = 3   if owner:IsValidLivingZombie() then  owner:EmitSound("npc/barnacle/barnacle_digesting1.wav", 75, 120)  owner:EmitSound("npc/barnacle/barnacle_digesting2.wav", 75, 120)  end  end  end)  end end 