            ang:RotateAroundAxis(ang:Right(), self.VMAng.x)                  ang:RotateAroundAxis(ang:Up(), self.VMAng.y)                 ang:RotateAroundAxis(ang:Forward(), self.VMAng.z)                  pos:Add(ang:Right() * (self.VMPos.x))                 pos:Add(ang:Forward() * (self.VMPos.y))                 pos:Add(ang:Up() * (self.VMPos.z))             end              return pos, ang         end     end            AccessorFuncDT(wept, "ReloadAmmoDelay", "Float", 8)     AccessorFuncDT(wept, "ReloadAmmoDropDown", "Float", 9)     AccessorFuncDT(wept, "ReloadAmmoAfterReload", "Float", 10)     AccessorFuncDT(wept, "Overheat", "Float", 11)     AccessorFuncDT(wept, "Mode", "Float", 12)      function wept:Deploy()         BaseClass.Deploy(self)                    self:SetReloadAmmoDropDown(0)         self:SetReloadAmmoDelay(0)         self:SetReloadAmmoAfterReload(0)          return true     end      function wept:SendReloadAnimation()         BaseClass.SendReloadAnimation(self)          if CLIENT then             self.IsReloadingAnim = true         end          local reloadspeed = self.ReloadSpeed * self:GetReloadSpeedMultiplier()         self:SetReloadAmmoDropDown(CurTime() + self:SequenceDuration() * (0.2 / reloadspeed))         self:SetReloadAmmoDelay(CurTime() + self:SequenceDuration() * (0.83 / reloadspeed))         self:SetReloadAmmoAfterReload(CurTime() + self:SequenceDuration() * (1.1 / reloadspeed))     end      wept.ShowWorldModel = false     wept.ShowViewModel = false      wept.HoldType = "ar2"      wept.Primary.Sound = Sound("weapons/zs_dds/zs_brown.ogg")     wept.Primary.Damage = wept.Primary.Damage * (29/26)     wept.Primary.NumShots = 1     wept.Primary.Delay = 0.09      wept.Primary.ClipSize = 125      wept.Primary.Gesture = ACT_HL2MP_GESTURE_RANGE_ATTACK_AR2     wept.ReloadGesture = ACT_HL2MP_GESTURE_RELOAD_AR2      wept.Recoil = 0      wept.ConeMax = 10     wept.ConeMin = 3.0      wept.IronSightsPos = Vector(-4.06, -2.619, 2.069)     wept.IronSightsAng = Vector(0, 0, 0)      function wept:EmitFireSound()         self:EmitSound(self.Primary.Sound, 75, math.random(95, 105), 1.0, CHAN_WEAPON)         self:EmitSound("weapons/sg552/sg552-1.wav", 75, 105, 0.2, CHAN_WEAPON + 1)     end      function wept:ShootBullets(dmg, numbul, cone)         BaseClass.ShootBullets(self, dmg, numbul, cone)          if self:GetMode() ~= 1 then             self:SetMode(1)         end     end      function wept:GetCone()         return BaseClass.GetCone(self) * (self:GetWeaponBuildup() >= 0.9 and 1.25 or 1) * (self:GetFireMode() == 1 and 0.83 or 1)     end      function wept:GetFireDelay()         return self.Primary.Delay * (self:GetWeaponBuildup() >= 0.9 and 0.6 or 1) * (self:GetFireMode() == 1 and 1.33 or 1)     end      function wept:Think()         self.BaseClass.Think(self)          local owner = self:GetOwner()         if self:GetReloadFinish() >= 1 and self:GetWeaponBuildup() >= 0.01 then             self:SetWeaponBuildup(math.max(0, self:GetWeaponBuildup() - FrameTime() * 0.08))         end          if owner:IsHolding() or owner:GetBarricadeGhosting() or self:GetReloadFinish() >= 1 then             self:SetNextPrimaryFire(CurTime() + 0.75)             self:SetMode(0)             return         end          if owner:KeyReleased(IN_ATTACK) and self:GetReloadFinish() == 0 then             self:SetMode(0)         end          if IsValid(owner) then             if self:GetMode() == 1 and self:Clip1() >= 1 then                 local fm_scalar = self:GetFireMode() == 1 and 0.05 or 0.072                  self:SetWeaponBuildup(math.min(1, self:GetWeaponBuildup() + FrameTime() * fm_scalar))             else                 self:SetWeaponBuildup(math.max(0, self:GetWeaponBuildup() - FrameTime() * 0.05))             end         end     end      function wept:PrimaryAttack()         BaseClass.PrimaryAttack(self)     end     function wept:SecondaryAttack()         BaseClass.SecondaryAttack(self)     end      wept.WeaponBuildup = {         Colour = {250, 150, 30, 255},         Name = "Heat"     } end) branch.Killicon = "weapon_zs_browning" branch.CollectiveName = "Browning"  SWEP.WeaponBuildup = {  Colour = {150, 150, 30, 255},  Name = "Assists" }  function SWEP:PrimaryAttack()  if not self:CanPrimaryAttack() then return end   self:SetNextPrimaryFire(CurTime() + self:GetFireDelay() * (self:Clip1() == 2 and 5 or 1))   self:EmitFireSound()  self:TakeAmmo()  self:ShootBullets(self.Primary.Damage, self.Primary.NumShots, self:GetCone())  self.IdleAnimation = CurTime() + self:SequenceDuration() end  function SWEP:EmitFireSound()  self:EmitSound(self.Primary.Sound)   if self:Clip1() == 1 then  self:EmitSound("weapons/sg552/sg552-1.wav", 70, 45, 0.95, CHAN_AUTO)  end end  function SWEP:OnAssist(zombie)  local assister = self:GetOwner()   if assister:IsValid() then  self:SetWeaponBuildup(math.min(1, self:GetWeaponBuildup() + 0.075))  end end  function SWEP:SecondaryAttack()  if not self:CanPrimaryAttack() then return end      local fm = self:GetFireMode()  if fm == 1 then         self:SetNextPrimaryFire(CurTime() + self:GetFireDelay() * 1.5)          if self:GetWeaponBuildup() >= 0.1 then             local owner = self:GetOwner()              self:EmitSound("weapons/zs_dds/zs_dd.ogg", 75, math.random(65, 70), 0.6)             self:EmitSound("weapons/zs_dds/zs_mini.ogg", 75, math.random(110, 120), 1, CHAN_WEAPON + 1)             self:SendWeaponAnimation()             self:TakeAmmo()             self:TakeAmmo()             self:SetWeaponBuildup(math.max(self:GetWeaponBuildup() - 0.065, 0))             owner:DoAttackEvent()              if SERVER then                 self:ShootProjectile(self.Primary.Damage * 2, self:GetCone()/2.5, "projectile_juggernaut", 1000,                      function(angle) angle:RotateAroundAxis(angle:Up(), 90) end                 )             end         end     else         BaseClass.SecondaryAttack(self)     end end  function SWEP:FireModeChanged(fm) end  function SWEP:PlayFireModeSwitchSound(fm)     fm = tonumber(fm)     if CLIENT then         if fm == 1 then             surface.PlaySound("weapons/c4/c4_disarm.wav")         else             surface.PlaySound("weapons/smg1/switch_burst.wav")         end     end end   function SWEP:ShootBullets(dmg, numbul, cone)  local owner = self:GetOwner()  local zeroclip = self:Clip1() == 0   self:SendWeaponAnimation()  owner:DoAttackEvent()  if self.Recoil > 0 then  local r2 = zeroclip and 1 or 0  local r = math.Rand(0.8, 1) * r2  owner:ViewPunch(Angle(r * -self.Recoil, r * (math.random(2) == 1 and -1 or 1) * self.Recoil, (r2 - r) * (math.random(2) == 1 and -1 or 1) * self.Recoil))  end   if SERVER and zeroclip then  for i = 1, 8 do  self:ShootProjectile(self.Primary.Damage * 1.25, self:GetCone()/2.5, "projectile_juggernaut", 1000,   function(angle) angle:RotateAroundAxis(angle:Up(), 90) end  )  end  end   DO_BULLET_LAG_COMP = true  owner:FireBulletsLua(owner:GetShootPos(), owner:GetAimVector(), cone, numbul, dmg, nil, self.Primary.KnockbackScale, self.TracerName, self.BulletCallback, self.Primary.HullSize, nil, self.Primary.MaxDistance, nil, self)  DO_BULLET_LAG_COMP = nil end 