SWEP.PrintName = "Medic Cloud Bomb" SWEP.Description = "Explodes in to a cloud of healing gas. Heals 2HP, every second, for 10 seconds. Press RELOAD to alert humans you're throwing one."  SWEP.Base = "weapon_zs_basethrown"  SWEP.ViewModel = "models/weapons/c_grenade.mdl" SWEP.WorldModel = "models/healthvial.mdl"  SWEP.ShowViewModel = true SWEP.ShowWorldModel = false  SWEP.Primary.Ammo = "mediccloudbomb"  SWEP.MaxStock = 8  function SWEP:Reload()     if self:GetNextSecondaryFire() > CurTime() then return end     self:SetNextSecondaryFire(CurTime() + 8)      local owner = self:GetOwner()      if SERVER then         local worldhint = ents.Create("point_worldhint")         if worldhint:IsValid() then             worldhint:SetPos(owner:GetShootPos() + owner:GetAimVector() * 60)             worldhint:Spawn()              worldhint:SetViewable(TEAM_HUMAN)             worldhint:SetRange(512)             worldhint:SetHint("Medic Cloud Bomb Here!")              timer.Simple(4, function()                 if IsValid(worldhint) then                     worldhint:Remove()                 end             end)         end     end end