nd", rel = "body", pos = Vector(0, 0.3, 1.462), angle = Angle(0, 0, -5.197), size = Vector(0.07, 0.136, 0.059), color = Color(220, 100, 100, 255), surpresslightning = false, material = "models/props_canal/metalcrate001d", skin = 0, bodygroup = {} }     } end  SWEP.Base = "weapon_zs_base"  SWEP.HoldType = "revolver"  SWEP.ViewModel = "models/weapons/cstrike/c_pist_p228.mdl" SWEP.WorldModel = "models/weapons/w_pist_p228.mdl" SWEP.UseHands = true  SWEP.ReloadSound = Sound("") SWEP.Primary.Sound = Sound("weapons/zs_dds/zs_raygun.ogg")  SWEP.Primary.ClipSize = 30 GAMEMODE:SetupDefaultClip(SWEP.Primary)  SWEP.Primary.Damage = 73 SWEP.Primary.NumShots = 1 SWEP.Primary.Delay = 0.4  SWEP.RequiredClip = 2  SWEP.Primary.Automatic = true SWEP.Primary.Ammo = "chemical"  SWEP.ConeMax = 3.5 SWEP.ConeMin = 1.5  SWEP.WalkSpeed = SPEED_FAST  SWEP.TracerName = "raygun_trail_2"  SWEP.Radius = 40 SWEP.Taper = 0.6  SWEP.Tier = 5  SWEP.ReloadSpeed = 0.8  SWEP.FireAnimSpeed = 0.5  SWEP.TreatDamageTypeForRounds = DMG_DISSOLVE  SWEP.ReloadConsumption = 0.75  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.05)  function SWEP.BulletCallback(attacker, tr, dmginfo)     dmginfo:SetDamageType(DMG_DISSOLVE)      if SERVER then         local basedmg = 27          timer.Simple(0.03, function()             local act_wep = attacker:GetActiveWeapon()              util.BlastDamagePlayer(act_wep, attacker, tr.HitPos, act_wep.Radius, basedmg, DMG_DISSOLVE, act_wep.Taper)         end)     end      if tr.HitWorld then         util.Decal("FadingScorch", tr.HitPos + tr.HitNormal, tr.HitPos - tr.HitNormal)     end      return {impact = false} end  function SWEP:EmitFireSound()  self:EmitSound(self.Primary.Sound, 75, math.random(99, 101), 0.75) end  function SWEP:SendReloadAnimation()  self:SendWeaponAnim(ACT_VM_RELOAD)      local anim_duration = self:SequenceDuration()     local reload_speed = self.ReloadSpeed * self:GetReloadSpeedMultiplier()      timer.Simple((anim_duration * 0.1) / reload_speed, function()         if not IsValid(self) then return end          self:SendWeaponAnim(ACT_VM_DRAW)         self:EmitSound("weapons/zs_dds/zs_rayre.ogg")         self:GetOwner():GetViewModel():SetPlaybackRate(reload_speed / 1.5)     end)      timer.Simple((anim_duration * 0.5) / reload_speed, function()         if not IsValid(self) then return end          self:SendWeaponAnim(ACT_VM_DRAW)         self:GetOwner():GetViewModel():SetPlaybackRate(reload_speed / 2.25)     end)      timer.Simple((anim_duration * 1) / reload_speed, function()         if not IsValid(self) then return end          self:SendWeaponAnim(ACT_VM_IDLE)         self:GetOwner():GetViewModel():SetPlaybackRate(1)         self:EmitSound("weapons/zs_dds/zs_raydep.ogg", 75, 100)     end) end  local ghostlerp = 0  function SWEP:CalcViewModelView(vm, oldpos, oldang, pos, ang)     if self:GetOwner():GetBarricadeGhosting() or self:GetReloadFinish() > 0 then         ghostlerp = math.min(1, ghostlerp + FrameTime() * 1)     elseif ghostlerp > 0 then         ghostlerp = math.max(0, ghostlerp - FrameTime() * 1.5)     end      if ghostlerp > 0 then         ang:RotateAroundAxis(ang:Right(), -12 * ghostlerp)     end      return pos, ang end