AddCSLuaFile()  SWEP.Base = "weapon_zs_fastzombie" local BaseClass = baseclass.Get("weapon_zs_fastzombie")  SWEP.PrintName = "Parasite"  SWEP.ViewModel = Model("models/weapons/v_nFZA.mdl") SWEP.WorldModel = ""  if CLIENT then  SWEP.ViewModelFOV = 70 end  SWEP.MeleeDelay = 0 SWEP.MeleeReach = 48 SWEP.MeleeDamage = 12 SWEP.MeleeForceScale = 0.1 SWEP.MeleeSize = 1.5 SWEP.MeleeDamageType = DMG_SLASH SWEP.Primary.Delay = 0.4  SWEP.SlowMeleeDelay = 0.9 SWEP.SlowMeleeDamage = 22  SWEP.PounceDamage = 28 SWEP.PounceDamageVsPlayerMul = 0.3 SWEP.PounceDamageType = DMG_IMPACT SWEP.PounceReach = 28 SWEP.PounceSize = 15 SWEP.PounceStartDelay = 0.65 SWEP.PounceDelay = 3.5 SWEP.PounceVelocity = 850  SWEP.RoarTime = 2.0  SWEP.Secondary.Automatic = false  function SWEP:ApplyMeleeDamage(ent, trace, damage)  if SERVER and ent:IsPlayer() then  local owner = self:GetOwner()  if owner:Health() < owner:GetMaxHealth() then  owner:SetHealth(math.min(owner:Health() + damage/2.5, owner:GetMaxHealth()))  end  end   BaseClass.ApplyMeleeDamage(self, ent, trace, damage) end  function SWEP:PlayClimbSound()  self:EmitSound("player/footsteps/metalgrate"..math.random(4)..".wav") end  function SWEP:PlayPounceHitSound()  self:EmitSound("physics/flesh/flesh_strider_impact_bullet1.wav")  self:EmitSound("npc/fast_zombie/wake1.wav", nil, nil, nil, CHAN_AUTO)   end function SWEP:PlaySwingEndSound()  self:EmitSound("NPC_FastZombie.Frenzy") end  function SWEP:PlaySlowSwingSound()  self.Owner:EmitSound("npc/antlion_guard/angry"..math.random(3)..".wav", 70, 60)  self.Owner:EmitSound("vo/ravenholm/monk_pain04.wav", 75, 55, 1, CHAN_AUTO)    end  function SWEP:PlayPounceStartSound()  self:EmitSound("npc/fast_zombie/leap1.wav", 75, math.random(70, 80), nil, CHAN_AUTO) end  function SWEP:PlayPounceSound()     self:EmitSound("npc/antlion_guard/growl_idle.wav")  self:EmitSound("npc/antlion_guard/angry".. math.random(1,3) ..".wav", 75, math.random(50, 90)) end  function SWEP:PlayHitSound()  self:EmitSound("NPC_FastZombie.AttackHit", 75, math.random(60, 70)) end  function SWEP:PlayMissSound()  self:EmitSound("NPC_FastZombie.AttackMiss", nil, nil, nil, CHAN_AUTO) end  function SWEP:PlayAttackSound() end  function SWEP:PlayIdleSound()  self:GetOwner():EmitSound("npc/fast_zombie/fz_frenzy1.wav", 75, math.random(60, 70))  self.Owner:EmitSound("npc/zombie_poison/pz_alert1.wav", 60, math.random(60, 70))  self:SetRoarEndTime(CurTime() + self.RoarTime) end  function SWEP:PlayAlertSound()  self:GetOwner():EmitSound("npc/fast_zombie/fz_frenzy1.wav", 75, math.random(60, 70))  self:SetRoarEndTime(CurTime() + self.RoarTime) end  function SWEP:PlaySwingEndSound()  self:EmitSound("npc/antlion_guard/angry"..math.random(3)..".wav", 75, 90) end  function SWEP:StartSwingingSound()  self:EmitSound("Weapon_Lacerator.Swinging") end  function SWEP:StopSwingingSound()  self:StopSound("Weapon_Lacerator.Swinging") end  if not CLIENT then return end  local matSkin = Material("models/barnacle/barnacle_sheet")  function SWEP:PreDrawViewModel(vm)  render.ModelMaterialOverride(matSkin)  render.SetColorModulation(0.6, 0.1, 0.1) end  function SWEP:ViewModelDrawn()  render.ModelMaterialOverride()  render.SetColorModulation(0.6, 0.1, 0.1) end   