vang.y = vang.p   self.BaseClass.DrawWorldModel(self) end SWEP.DrawWorldModelTranslucent = SWEP.DrawWorldModel  SWEP.BeamColor = Color(111, 215, 115) local beam1mat = Material("trails/electric") local glow = Material("effects/yellowflare") function SWEP:PostDrawViewModel(vm)  local time = math.cos(CurTime() * 0.3) * 720  local time2 = math.sin(CurTime() * 0.3) * 720  local vang = self.VElements.main.angle  vang.p = time % 360  vang.r = time2 % 360  vang.y = vang.p      local next_tp = self:GetTryTeleportTime()     if next_tp == 0 then return end      local length = 1 - (self:GetTryTeleportTime() - CurTime())/2   local attachposlocal = vm:WorldToLocal(vm:GetAttachment(1).Pos)  local attachpos = vm:LocalToWorld(attachposlocal + Vector(12, 1, 1)) + VectorRand() * 0.3      render.SetMaterial(beam1mat)  render.DrawBeam(attachpos, attachpos + VectorRand() * length * 12, 1, math.random(2, 10), 1 + math.random(5, 8), self.BeamColor)      render.SetMaterial(glow)     render.DrawSprite(attachpos, 30 * length, 30 * length, self.BeamColor)      local tp_pos = self:GetTPPos()     if tp_pos.x ~= 0 then         render.DrawBeam(attachpos, tp_pos, 1, math.random(2, 10), 1 + math.random(5, 8), self.BeamColor)         render.DrawSprite(tp_pos, 30 + 30 * length, 30 + 30 * length, self.BeamColor)     end end