etZombieClassTable().Name ~= "Shade" and CurTime() >= (hitent._NextLeadPipeEffect or 0) then             hitent._NextLeadPipeEffect = CurTime() + 1.5              local x = math.Rand(0.75, 1)             x = x * (math.random(2) == 2 and 1 or -1)              local ang = Angle(1 - x, x, 0) * 38             hitent:ViewPunch(ang)              local eyeangles = hitent:EyeAngles()             eyeangles:RotateAroundAxis(eyeangles:Up(), ang.yaw)             eyeangles:RotateAroundAxis(eyeangles:Right(), ang.pitch)             eyeangles.pitch = math.Clamp(ang.pitch, -89, 89)             eyeangles.roll = 0             hitent:SetEyeAngles(eyeangles)              if self:IsHeavy() then                 hitent:GiveStatus("debuff_frightened", 1)             end         end     end end 