SWEP.PrintName = "'Pollutor' Acid Rifle" SWEP.Description = "Launches acidic projectiles that have a chance to remove damage resistance."  SWEP.Base = "weapon_zs_baseproj"  SWEP.HoldType = "smg"  SWEP.ViewModel = "models/weapons/cstrike/c_smg_ump45.mdl" SWEP.WorldModel = "models/weapons/w_smg_ump45.mdl" SWEP.UseHands = true  SWEP.CSMuzzleFlashes = false  SWEP.Primary.ClipSize = 12 SWEP.Primary.Automatic = true SWEP.Primary.Ammo = "chemical" SWEP.Primary.Delay = 0.42 SWEP.Primary.DefaultClip = 12 SWEP.Primary.Damage = 39 SWEP.Primary.NumShots = 1  SWEP.ConeMax = 2.5 SWEP.ConeMin = 2  SWEP.WalkSpeed = SPEED_SLOW  SWEP.Tier = 3  SWEP.FireAnimSpeed = 0.4  SWEP.Taper = 0.6 SWEP.Radius = 35  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.04) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_ACID_MUL, 0.07, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_RADIUS, 4, 3)  SWEP.CorrosionMul = 1 SWEP.IgniteMul = 1 SWEP.IceSlowMul = 1  local branch = GAMEMODE:AddNewRemantleBranch(SWEP, 1, "'Scorch' Napalm Rifle", "Chance to ignite instead of corrode at a small damage decrease.", function(wept)  wept.Primary.Damage = wept.Primary.Damage * (31/39)  if SERVER then  wept.EntModify = function(self, ent)  ent:SetDTInt(5, 1)             ent.IgniteMul = self.IgniteMul  end  else  wept.VElements["bio++++++++++"].color = Color(255, 95, 0)  wept.VElements["bio++++++"].color = Color(255, 95, 0)  wept.WElements["bio++++++++++"].color = Color(255, 95, 0)  wept.WElements["bio++++++"].color = Color(255, 95, 0)  end end) branch.Colors = {Color(255, 190, 80), Color(255, 160, 50), Color(215, 120, 50), Color(175, 100, 40)} branch.NewNames = {"Hot", "Searing", "Torching"} branch.CollectiveName = "Pyro" GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_FIRE_DELAY, -0.04) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_FIRE_MUL, 0.07, 1) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_RADIUS, 4, 3)  branch = GAMEMODE:AddNewRemantleBranch(SWEP, 2, "'Glaze' Cryo Rifle", "Launches cryoblobs that slow zombies down at the cost of damage.", function(wept)  wept.Primary.Damage = wept.Primary.Damage * (28/39)   if SERVER then  wept.EntModify = function(self, ent)  ent:SetDTInt(5, 2)             ent.IceSlowMul = self.IceSlowMul  end  else  wept.VElements["bio++++++++++"].color = Color(100, 190, 230)  wept.VElements["bio++++++"].color = Color(100, 190, 230)  wept.WElements["bio++++++++++"].color = Color(100, 190, 230)  wept.WElements["bio++++++"].color = Color(100, 190, 230)  end end) branch.Colors = {Color(80, 210, 255), Color(50, 160, 255), Color(50, 130, 215), Color(40, 115, 175)} branch.NewNames = {"Cold", "Arctic", "Glacial"} branch.CollectiveName = "Cryo" GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_FIRE_DELAY, -0.04) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_ICE_MUL, 0.07, 1) GAMEMODE:AttachWeaponModifierToBranch(branch, WEAPON_MODIFIER_RADIUS, 4, 3)  function SWEP:EmitFireSound()  self:EmitSound("^weapons/mortar/mortar_fire1.wav", 70, math.random(88, 92), 0.65)  self:EmitSound("npc/barnacle/barnacle_gulp2.wav", 70, 70, 0.85, CHAN_AUTO + 20) end 