ning = false, material = "phoenix_storms/metal_plate", skin = 0, bodygroup = {} }  } end  SWEP.Base = "weapon_zs_basemelee"  SWEP.ViewModel = "models/weapons/c_crowbar.mdl" SWEP.WorldModel = "models/weapons/w_crowbar.mdl" SWEP.UseHands = true  SWEP.ShowViewModel = false SWEP.ShowWorldModel = false  SWEP.HoldType = "melee"  SWEP.MeleeDamage = 70 SWEP.LegDamage = 14 SWEP.MeleeRange = 58 SWEP.MeleeSize = 1.5 SWEP.Primary.Delay = 0.9  SWEP.SwingTime = 0.3 SWEP.SwingRotation = Angle(60, 0, 0) SWEP.SwingOffset = Vector(0, -50, 0) SWEP.SwingHoldType = "grenade"  SWEP.BlockRotation = Angle(0, 40, -20) SWEP.BlockOffset = Vector(-10, 7, -5)  SWEP.Stability = 20 SWEP.BlockReduction = 0.75  SWEP.PointsMultiplier = GAMEMODE.PulsePointsMultiplier SWEP.DeploySpeedMulti = 2  SWEP.AllowQualityWeapons = true  SWEP.Tier = 3  SWEP.StaminaUsage = 0.17  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.08, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_STAMINA_USAGE, -0.007) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_LEG_DAMAGE, 1, 1)  function SWEP:PlaySwingSound()  self:EmitSound("weapons/stunstick/stunstick_swing1.wav", 75, 100, 0.7) end  function SWEP:PlayHitSound()  self:EmitSound("weapons/stunstick/stunstick_impact1.wav", 75, 100, 0.7) end  function SWEP:PlayHitFleshSound()  self:EmitSound("weapons/stunstick/stunstick_fleshhit2.wav", 75, 100, 0.7) end  function SWEP:OnMeleeHit(hitent, hitflesh, tr)  if hitent:IsValid() and hitent:IsPlayer() then  local heavy = self:IsHeavy()   hitent:AddLegDamageExt(self.LegDamage * (heavy and 1 or 0.7), self:GetOwner(), self, SLOWTYPE_PULSE)   if SERVER then  local status = hitent:GiveStatus("dot_shock")  if status and status:IsValid() then  status:AddDamage(50 * (heavy and 1 or 0.7), self:GetOwner(), self)  end  end  end end  function SWEP:BashEffect(tr, ent)  if SERVER then  ent:AddLegDamageExt(self.LegDamage, self:GetOwner(), self, SLOWTYPE_PULSE)  end end