SWEP.Base = "weapon_zs_zombie" local BaseClass = baseclass.Get("weapon_zs_zombie")  SWEP.PrintName = "Puke Pus"  SWEP.Primary.Delay = 3.5  SWEP.ViewModel = "models/weapons/v_crowbar.mdl" SWEP.WorldModel = "models/Zombie/Poison.mdl"  SWEP.NextPuke = 0 SWEP.PukeLeft = 0  AccessorFuncDT(SWEP, "BallTime", "Float", 1)  function SWEP:Initialize()  self:HideViewAndWorldModel()   BaseClass.Initialize(self) end  function SWEP:PrimaryAttack()  if CurTime() < self:GetNextPrimaryFire() then return end  local owner = self:GetOwner()  local delay = owner:GetMeleeSpeedMul()  self:SetNextPrimaryFire(CurTime() + self.Primary.Delay * delay)   self.PukeLeft = 21   owner:EmitSound("npc/barnacle/barnacle_die2.wav")  owner:EmitSound("npc/barnacle/barnacle_digesting1.wav")  owner:EmitSound("npc/barnacle/barnacle_digesting2.wav") end  function SWEP:SecondaryAttack()  if CurTime() < self:GetNextPrimaryFire() or CurTime() < self:GetNextSecondaryFire() then return end   local owner = self:GetOwner()  local delay = owner:GetMeleeSpeedMul()   self:SetNextSecondaryFire(CurTime() + 3 * delay)  self:SetNextPrimaryFire(CurTime() + self.Primary.Delay * delay)   owner:EmitSound("npc/barnacle/barnacle_die2.wav", 75, 76)  owner:EmitSound("npc/barnacle/barnacle_digesting1.wav", 75, 66)  owner:EmitSound("npc/barnacle/barnacle_digesting2.wav", 75, 66)   self:SetBallTime(CurTime() + 0.9) end  function SWEP:Reload() end 