AddCSLuaFile() local BaseClass = baseclass.Get("weapon_zs_base")  SWEP.PrintName = "'Quicksilver' Semi-Auto Rifle" SWEP.Description = "A semi automatic sniper rifle. Has good fire rate, large clip size and a decent damage per shot."  SWEP.Slot = 3 SWEP.SlotPos = 0  if CLIENT then  SWEP.ViewModelFlip = false   SWEP.HUD3DBone = "v_weapon.g3sg1_Parent"  SWEP.HUD3DPos = Vector(-1.2, -5.75, -1)  SWEP.HUD3DAng = Angle(0, 0, 0)  SWEP.HUD3DScale = 0.015 end  SWEP.Base = "weapon_zs_base"  SWEP.HoldType = "ar2"  SWEP.ViewModel = "models/weapons/cstrike/c_snip_g3sg1.mdl" SWEP.WorldModel = "models/weapons/w_snip_g3sg1.mdl" SWEP.UseHands = true  SWEP.Primary.Sound = Sound(")weapons/g3sg1/g3sg1-1.wav") SWEP.Primary.Damage = 86 SWEP.Primary.NumShots = 1 SWEP.Primary.Delay = 0.44  SWEP.Primary.ClipSize = 8 SWEP.Primary.Automatic = false SWEP.Primary.Ammo = "357" GAMEMODE:SetupDefaultClip(SWEP.Primary)  SWEP.Primary.Gesture = ACT_HL2MP_GESTURE_RANGE_ATTACK_CROSSBOW SWEP.ReloadGesture = ACT_HL2MP_GESTURE_RELOAD_SHOTGUN  SWEP.ConeMax = 6.5 SWEP.ConeMin = 0  SWEP.IronSightsPos = Vector(11, -9, -2.2) SWEP.IronSightsAng = Vector(0, 0, 0)  SWEP.WalkSpeed = SPEED_SLOW  SWEP.Tier = 4 SWEP.OverkillFactor = 0.3  SWEP.FireModes = 2 SWEP.FireModeNames = {"Full-Zoom", "Half-Zoom"} SWEP.FireModeNames3D = {"FULL", "HALF"}  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.035) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_CLIP_SIZE, 1, 1) GAMEMODE:AddNewRemantleBranch(SWEP, 1, "'Mercurial' Birdshot Rifle", "Fires a spread of less accurate shots that deal more total damage, retains some damage resist penetration", function(wept)  wept.Primary.Damage = wept.Primary.Damage / 5  wept.Primary.NumShots = 6  wept.ConeMin = 3     wept.ResistanceAmmoAs = "pistol"     wept.OverkillFactor = nil     wept.AmmoTrinketOverride = 1.5 end).CollectiveName = "Birdshot"  function SWEP:IsScoped()  return self:GetIronsights() and self.fIronTime and self.fIronTime + 0.25 <= CurTime() end  function SWEP:FireModeChanged(fm)     fm = tonumber(fm)     self.IronsightsMultiplier = fm == 1 and 0.55 or 0.25 end  function SWEP:PlayFireModeSwitchSound(fm)     fm = tonumber(fm)     if CLIENT then         if fm == 1 then             surface.PlaySound("weapons/zoom/zoom_soundtest.wav")         else             surface.PlaySound("weapons/zoom/zoom_soundtest2.wav")         end     end end  if CLIENT then  SWEP.IronsightsMultiplier = 0.25   function SWEP:GetViewModelPosition(pos, ang)  if GAMEMODE.DisableScopes then return end   if self:IsScoped() then  return pos + ang:Up() * 256, ang  end   return BaseClass.GetViewModelPosition(self, pos, ang)  end   function SWEP:DrawHUDBackground()  if GAMEMODE.DisableScopes then return end   if self:IsScoped() then  self:DrawRegularScope()  end  end end  function SWEP:OnZombieKilled()  self:GetOwner():GiveStack("buff_stack_fervor") end  if not CLIENT then return end  function SWEP:Draw3DHUD(vm, pos, ang)  BaseClass.Draw3DHUD(self, vm, pos, ang)   local wid, hei = 180, 200  local x, y = wid * -0.6, hei * -0.5   cam.Start3D2D(pos, ang, self.HUD3DScale)  local owner = self:GetOwner()  local ownerstatus = owner:GetStatus("buff_stack_fervor")  if ownerstatus then  local text = ""  for i = 0, ownerstatus:GetDTInt(1)-1 do  text = text .. "x"  end  draw.SimpleTextBlurry(text, "ZS3D2DFontSmall", x + wid/2, y + hei * 0.15, Color(60, 175, 30, 230), TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)  end  cam.End3D2D() end