SWEP.PrintName = "Rune Watcher"  SWEP.Base = "weapon_zs_zombie" SWEP.WorldModel = "models/crow.mdl"  SWEP.IsCrow = true  SWEP.Primary.Delay = 3.5 SWEP.MeleeDelay = 0.2  AccessorFuncDT(SWEP, "NextVisibility", "Float", 15)  function SWEP:Initialize()  if CLIENT then  self:SCKInit()  end end  function SWEP:PrimaryAttack()     local owner = self:GetOwner()     if CurTime() < self:GetNextPrimaryFire() or owner:IsFrozen() or owner:IsOnGround() then return end      local armdelay = owner:GetMeleeSpeedMul()      self:SetNextPrimaryFire(CurTime() + self.Primary.Delay * armdelay)     self:StartSwinging()     self:SetNextVisibility(CurTime() + 4) end  function SWEP:Swung()  if not IsFirstTimePredicted() then return end   if SERVER then  self:MakeShard()  end end  function SWEP:PlayAttackSound()  self:EmitSound("npc/crow/crow" .. math.random(2, 3) .. ".wav", 85, 75) end  function SWEP:SecondaryAttack() end  function SWEP:Reload() end