urpresslightning = false, material = "phoenix_storms/concrete2", skin = 0, bodygroup = {} }  } end).CollectiveName = "Limbo" local branch = GAMEMODE:AddNewRemantleBranch(SWEP, 2, "'Flatline' Twin Burst Rifle", "Fires a two shot burst slowly, but more accurately, with a higher headshot multiplier. Hitting twice in a burst deals more total damage.", function(wept)  wept.Primary.Damage = wept.Primary.Damage * 1.23  wept.Primary.Delay = wept.Primary.Delay * 3.6  wept.Primary.BurstShots = 2      wept.HeadshotMulti = wept.HeadshotMulti * 1.075   wept.ConeMin = wept.ConeMin * (0.85/1.275)  wept.ConeMax = wept.ConeMax * 0.85      wept.Primary.ClipSize = 20   wept.FireModeNames = {"2-Round Burst", "Semi-Auto"}  wept.FireModeNames3D = {"BRST", "SEMI"}      wept.ReloadConsumption = nil   wept.PrimaryAttack = function(self)  if not self:CanPrimaryAttack() then return end  local fm = self:GetFireMode()   self:SetNextPrimaryFire(CurTime() + self:GetFireDelay(fm == 1 and 2.55 or 1))  self:EmitFireSound()   if fm == 0 then  self:SetNextShot(CurTime())  self:SetShotsLeft(self.Primary.BurstShots)  else  self:TakeAmmo()  self:ShootBullets(self.Primary.Damage, self.Primary.NumShots, self:GetCone())  end   self.IdleAnimation = CurTime() + self:SequenceDuration()  end   wept.Think = function(self)  BaseClass.Think(self)   local shotsleft = self:GetShotsLeft()  if shotsleft > 0 and CurTime() >= self:GetNextShot() then  self:SetShotsLeft(shotsleft - 1)  self:SetNextShot(CurTime() + self:GetFireDelay(12))   if self:Clip1() > 0 and self:GetReloadFinish() == 0 then  self:EmitFireSound()  self:TakeAmmo()  self:ShootBullets(self.Primary.Damage, self.Primary.NumShots, self:GetCone())   self.IdleAnimation = CurTime() + self:SequenceDuration()  else  self:SetShotsLeft(0)  end  end          self:NextThink(CurTime())         return true  end      function wept:SetNextShot(nextshot)         self:SetDTFloat(5, nextshot)     end      function wept:GetNextShot()         return self:GetDTFloat(5)     end      function wept:SetShotsLeft(shotsleft)         self:SetDTInt(1, shotsleft)     end      function wept:GetShotsLeft()         return self:GetDTInt(1)     end      wept.RecentHits = {}     wept.RecentHitsDouble = {}     function wept.BulletCallback(attacker, tr, dmginfo, curpen)         local ent = tr.Entity          if SERVER and ent and ent:IsValidLivingZombie() and curpen == 0 then             local inf = dmginfo:GetInflictor()              if inf.RecentHits[ent] and inf.RecentHits[ent] > CurTime() then                 dmginfo:ScaleDamage(1.1)             end              inf.RecentHits[ent] = CurTime() + 0.2         end     end end) branch.CollectiveName = "Flatline" branch.Colors = {Color(210, 230, 160), Color(160, 180, 110), Color(150, 170, 75), Color(140, 150, 40)}  function SWEP:EmitFireSound()  self:EmitSound("weapons/zs_scar/scar_fire1.ogg", 80, math.random(85, 90), 1, CHAN_WEAPON) end  function SWEP:SetHitStacks(stacks)  self:SetDTInt(9, stacks) end  function SWEP:GetHitStacks()  return self:GetDTInt(9) end  function SWEP:FinishReload()  self:SetHitStacks(0)  BaseClass.FinishReload(self) end  function SWEP:GetCone()  return BaseClass.GetCone(self) * (1 - self:GetHitStacks()/35) end  function SWEP:Deploy()  self:SetHitStacks(0)  return BaseClass.Deploy(self) end  function SWEP.BulletCallback(attacker, tr, dmginfo)  local ent = tr.Entity  if SERVER then  local wep = attacker:GetActiveWeapon()  if ent:IsValidZombie() then  wep:SetHitStacks(wep:GetHitStacks() + 1)  end  end end 