SWEP.PrintName = "Great Scythe" SWEP.Description = "A scythe that can hit through multiple zombies. Has a 3 hit combo, and deals extra head damage."  SWEP.Base = "weapon_zs_basemelee"  SWEP.HoldType = "melee2"  SWEP.DamageType = DMG_CLUB  SWEP.ViewModel = "models/weapons/c_crowbar.mdl" SWEP.WorldModel = "models/weapons/w_crowbar.mdl" SWEP.UseHands = true  SWEP.MeleeDamage = 98 SWEP.MeleeRange = 79 SWEP.MeleeSize = 3.5 SWEP.MeleeKnockBack = 0  SWEP.Primary.Delay = 1.25  SWEP.WalkSpeed = SPEED_SLOWER  SWEP.SwingRotation = Angle(0, -120, -70) SWEP.SwingOffset = Vector(0, 30, -40) SWEP.SwingTime = 0.35 SWEP.SwingHoldType = "melee"  SWEP.Tier = 4  SWEP.AllowQualityWeapons = true  SWEP.BlockRotation = Angle(0, 60, -50) SWEP.BlockOffset = Vector(-25, -2, 10)  SWEP.Stability = 50 SWEP.BlockReduction = 0.5  SWEP.Cleave = true SWEP.CleaveTaper = 0.55  SWEP.RegionHeadMeleeMulti = 1.35  SWEP.StaminaUsage = 0.16  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.12, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_STAMINA_USAGE, -0.004, 1) GAMEMODE:AddNewRemantleBranch(SWEP, 1, "Great Reaper", "Deals a lot less damage but gains up to 2 reaper and fervor stacks", function(wept)  wept.MeleeDamage = wept.MeleeDamage * 0.75   wept.OnZombieKilled = function(self, zombie, total, dmginfo)  self:GetOwner():GiveStack(math.random(2) == 1 and "buff_stack_reaper" or "buff_stack_fervor", 2)  end end).CollectiveName = "Reaper"  function SWEP:PlaySwingSound()  self:EmitSound("weapons/iceaxe/iceaxe_swing1.wav", 75, math.random(55, 65)) end  function SWEP:PlayHitSound()  self:EmitSound("ambient/machines/slicer"..math.random(4)..".wav", 75, 90)  self:EmitSound("physics/metal/metal_computer_impact_hard2.wav", 65, 90, 0.5, CHAN_WEAPON + 1) end  function SWEP:AfterSwing()  local combo = self:GetDTInt(10)  local owner = self:GetOwner()   local armdelay = owner:GetMeleeSpeedMul()      local swing_del =   combo == 2 and 1.25 or                         combo == 1 and 0.7 or                          combo == 0 and 0.2   self:SetNextPrimaryFire(CurTime() - self.SwingTime +  self.Primary.Delay * swing_del * armdelay  )      self.SwingRotation =    combo == 0 and Angle(0, -120, -70) or                              combo == 1 and Angle(40, -45, -170) or                             combo >= 2 and Angle(0, -120, -70)      self.SwingOffset =      combo == 0 and Vector(0, 30, -40) or                              combo == 1 and Vector(0, 0, -10) or                             combo >= 2 and Vector(0, 30, -40)   self:SetDTInt(10, combo >= 2 and 0 or combo + 1) end  function SWEP:BeforeSwing(damagemultiplier)  local combo = self:GetDTInt(10)   if combo == 2 then  damagemultiplier = damagemultiplier * 1.35  end   return damagemultiplier end