AddCSLuaFile()  SWEP.PrintName = "Shovel" SWEP.Description = "A long shovel that instantly kills zombies that are knocked down."  if CLIENT then  SWEP.ViewModelFOV = 70   SWEP.ShowViewModel = true  SWEP.ShowWorldModel = false end  SWEP.Base = "weapon_zs_basemelee"  SWEP.HoldType = "melee2"  SWEP.DamageType = DMG_CLUB  SWEP.ViewModel = "models/weapons/c_shovel/c_shovel.mdl" SWEP.WorldModel = "models/props_junk/shovel01a.mdl" SWEP.UseHands = true  SWEP.MeleeDamage = 73 SWEP.MeleeRange = 73 SWEP.MeleeSize = 1.5 SWEP.MeleeKnockBack = 230  SWEP.Primary.Delay = 1.2  SWEP.Tier = 2  SWEP.WalkSpeed = SPEED_SLOWER  SWEP.SwingRotation = Angle(20, 0, -30) SWEP.SwingOffset = Vector(0, -30, -10) SWEP.SwingTime = 0.65 SWEP.SwingHoldType = "melee"  SWEP.AllowQualityWeapons = true  SWEP.Stability = 50 SWEP.BlockReduction = 0.5 SWEP.StaminaUsage = 0.26  SWEP.BlockRotation = Angle(0, 20, -60) SWEP.BlockOffset = Vector(5, 7, 10)  SWEP.HitAnim = ACT_VM_MISSCENTER  SWEP.OverkillFactor = 0.5 SWEP.DismantleDiv = 3  SWEP.TreatDamageTypeForRounds = DMG_DIRECT  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.12, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_STAMINA_USAGE, -0.006, 1) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_MELEE_IMPACT_DELAY, -0.09, 1)  function SWEP:PlaySwingSound()  self:EmitSound("weapons/iceaxe/iceaxe_swing1.wav", 75, math.random(65, 70)) end  function SWEP:PlayHitSound()  self:EmitSound("weapons/melee/shovel/shovel_hit-0"..math.random(4)..".ogg") end  function SWEP:PlayHitFleshSound()  self:EmitSound("physics/body/body_medium_break"..math.random(2, 4)..".wav") end  function SWEP:PostOnMeleeHit(hitent, hitflesh, tr)  if hitent:IsValid() and hitent:IsPlayer() and not hitent:GetStatus("redmarrow") and hitent.Revive and hitent.Revive:IsValid() and gamemode.Call("PlayerShouldTakeDamage", hitent, self:GetOwner()) then  hitent:TakeSpecialDamage(hitent:Health(), DMG_DIRECT, self:GetOwner(), self, tr.HitPos)  end end