            SWEP.WElements[ k ].def_pos = Vector( v.pos.x, v.pos.y, v.pos.z )             if v.angle then                 SWEP.WElements[ k ].def_angle = Angle( v.angle.p, v.angle.y, v.angle.r )             end         end     end end  SWEP.Base = "weapon_zs_basemelee"  SWEP.DamageType = DMG_SLASH  SWEP.ViewModel = "models/weapons/c_crowbar.mdl" SWEP.WorldModel = "models/player/zombie_classic_hbfix.mdl" SWEP.NoDroppedWorldModel = true  SWEP.InvisModel = true    SWEP.ZombieOnly = true SWEP.MeleeDamage = 38 SWEP.MeleeRange = 82 SWEP.MeleeSize = 2 SWEP.Primary.Delay = 0.6  SWEP.StaminaUsage = 0  SWEP.HitGesture = ACT_HL2MP_GESTURE_RANGE_ATTACK_MELEE2 SWEP.MissGesture = SWEP.HitGesture  SWEP.HitDecal = "Manhackcut" SWEP.HitAnim = ACT_VM_MISSCENTER  SWEP.FakeAttackMulti = 2 SWEP.TryAlt = true  function SWEP:PlaySwingSound()     self:EmitSound("weapons/knife/knife_slash"..math.random(2)..".wav", 72, math.Rand(55, 65)) end  function SWEP:PlayHitSound()     self:EmitSound("ambient/machines/slicer"..math.random(4)..".wav", 75, 60, nil, CHAN_AUTO) end  function SWEP:PlayHitFleshSound()     self:EmitSound("physics/flesh/flesh_squishy_impact_hard"..math.random(4)..".wav")     self:EmitSound("physics/body/body_medium_break"..math.random(2, 4)..".wav") end  local vec_up = Vector(0, 0, 1) function SWEP:PullHuman( ent )     local owner = self:GetOwner()      if SERVER then         local e = EffectData()             e:SetRadius( owner:EntIndex() )             e:SetOrigin( ent:GetShootPos() )             e:SetEntity( ent )         util.Effect( "tyrant_grab", e, true, true )          ent:KnockDown(0.4)          local status = ent:GiveStatus("tyrantpull")         if status and status:IsValid() then             status:SetDamage(7)             status:SetPuller(owner)             status.Tight = true         end          owner:SetGroundEntity(NULL)         owner:SetVelocity(vec_up * 410 + owner:GetAimVector() * 100)     end      owner:EmitSound("zombiesurvival/airburst.ogg", 85, 120)     owner:DoCustomAnimEvent( PLAYERANIMEVENT_ATTACK_SECONDARY, 1 )     owner:EmitSound( "npc/metropolice/vo/chuckle.wav", 70, math.Rand(55, 65) ) end  function SWEP:BuffZombies()     local owner = self:GetOwner()      if SERVER then         local e = EffectData()             e:SetOrigin( owner:GetPos() )         util.Effect( "tyrant_buff", e, true, true )          local center = owner:WorldSpaceCenter()          for _, ent in pairs(ents.FindInSphere(center, 140)) do             if ent:IsValidLivingZombie() and WorldVisible(ent:WorldSpaceCenter(), center) and ent:MetabuffEligiblity() then                 local status = ent:GiveStatus("meta_unstable")                 status.Applier = owner             end         end     end      owner:DoCustomAnimEvent( PLAYERANIMEVENT_ATTACK_GRENADE, 1 )     owner:EmitSound( "npc/metropolice/takedown.wav", 70, math.Rand(55, 65) ) end  function SWEP:CanPrimaryAttack()     return self:GetNextPrimaryFire() <= CurTime() and not self:IsSwinging() and not self:IsWinding() end  function SWEP:CanSecondaryAttack()     return self:GetNextSecondaryFire() <= CurTime() and not self:IsSwinging() and not self:IsWinding() end  function SWEP:SecondaryAttack()     if not self:CanSecondaryAttack() then return end     self:SetNextSecondaryAttack( CurTime() + 4 )      local owner = self:GetOwner()     local tr = owner:CompensatedMeleeTrace(600, 10)     if tr and tr.Entity and tr.Entity:IsValidLivingHuman() then         self:PullHuman( tr.Entity )         self:SetNextPrimaryFire( CurTime() + 0.5 )     else         self:BuffZombies()         self:SetNextPrimaryFire( CurTime() + 0.5 )     end end  function SWEP:PostOnMeleeHit(hitent, hitflesh, tr)     if hitent:IsValidLivingHuman() and hitent:GetStatus("tyrantpull") then         hitent:TakeSpecialDamage(hitent:Health() + 100, DMG_SLASH, self:GetOwner(), self, tr.HitPos)     end end  function SWEP:SetNextAttack()     local owner = self:GetOwner()     local armdelay = owner:GetMeleeSpeedMul()     self:SetNextPrimaryFire(CurTime() + self.Primary.Delay * armdelay) end  if CLIENT then     local math_random = math.random     local math_Rand = math.Rand     local dummy_angle = Angle( 1, 0, 0 )      function SWEP:DrawWorldModel()         local owner = self:GetOwner()         if owner:IsValid() and (owner.ShadowMan or owner.SpawnProtection) then return end          for k, v in pairs( self.WElements ) do             if v and v.pos and v.def_pos and v.shake then                 if math_random( 30 ) == 30 then                     self.WElements[ k ].pos = v.def_pos + VectorRand() * math_Rand( -0.5, 0.5 )                     if v.angle and v.def_angle then                         self.WElements[ k ].angle = v.def_angle + dummy_angle * math_Rand( -3, 3 )                     end                 else                     self.WElements[ k ].pos = v.def_pos                     if v.angle and v.def_angle then                         self.WElements[ k ].angle = v.def_angle                     end                 end             end         end          self:SCKWorldModel()     end end