AddCSLuaFile()  SWEP.PrintName = "Stun Baton" SWEP.Description = "This baton has the ability to slow zombies with pulse slowing technology and it gains +25% extra points."  if CLIENT then  SWEP.ViewModelFOV = 50 end  SWEP.Base = "weapon_zs_basemelee"  SWEP.ViewModel = "models/weapons/c_stunstick.mdl" SWEP.WorldModel = "models/weapons/w_stunbaton.mdl" SWEP.UseHands = true  SWEP.HoldType = "melee"  SWEP.MeleeDamage = 38 SWEP.LegDamage = 20 SWEP.MeleeRange = 56 SWEP.MeleeSize = 1.5 SWEP.Primary.Delay = 0.9  SWEP.SwingTime = 0.25 SWEP.SwingRotation = Angle(60, 0, 0) SWEP.SwingOffset = Vector(0, -50, 0) SWEP.SwingHoldType = "grenade"  SWEP.BlockRotation = Angle(0, 40, -20) SWEP.BlockOffset = Vector(-10, 7, -5)  SWEP.Stability = 50 SWEP.BlockReduction = 0.6  SWEP.PointsMultiplier = GAMEMODE.PulsePointsMultiplier  SWEP.AllowQualityWeapons = true  SWEP.StaminaUsage = 0.16  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_FIRE_DELAY, -0.09) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_STAMINA_USAGE, -0.005) GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_LEG_DAMAGE, 2)  function SWEP:PlaySwingSound()  self:EmitSound("weapons/stunstick/stunstick_swing1.wav", 75, 100, 0.7) end  function SWEP:PlayHitSound()  self:EmitSound("weapons/stunstick/stunstick_impact1.wav", 75, 100, 0.7) end  function SWEP:PlayHitFleshSound()  self:EmitSound("weapons/stunstick/stunstick_fleshhit2.wav", 75, 100, 0.7) end  function SWEP:OnMeleeHit(hitent, hitflesh, tr)  if hitent:IsValid() and hitent:IsPlayer() then  local heavy = self:IsHeavy()   hitent:AddLegDamageExt(self.LegDamage * (heavy and 1 or 0.7), self:GetOwner(), self, SLOWTYPE_PULSE)  end end  function SWEP:BashEffect(tr, ent)  if SERVER then  ent:AddLegDamageExt(self.LegDamage, self:GetOwner(), self, SLOWTYPE_PULSE)  end end