      local owner = self:GetOwner()         if not owner:CooldownReady("Procyon") or self:GetWeaponBuildup() < 1 then return end         owner:AddNewCooldown("Procyon", self.ChargeCooldown)         self:SetWeaponBuildup(0)          if SERVER then             local light = ents.Create(self.ChargeProjectile)             if light:IsValid() then                 light:SetPos(owner:GetShootPos())                 light:SetAngles(owner:EyeAngles())                 light:SetOwner(owner)                 light.ProjDamage = self.MeleeDamage * (owner.ProjectileDamageMul or 1)                 light.ProjSource = self                 light.Team = owner:Team()                 light:Spawn()                  local phys = light:GetPhysicsObject()                 if phys:IsValid() then                     phys:Wake()                      phys:ApplyForceCenter(owner:GetAimVector() * 700)                     phys:AddAngleVelocity(Vector(-2000,0,0))                 end             end         end     end      wept.Primary.Delay = wept.Primary.Delay * 0.65     wept.MeleeDamage = wept.MeleeDamage * (189/250)     wept.CleaveTaper = 0.5     wept.SwingTime = wept.SwingTime * 0.5     wept.MeleeRange = wept.MeleeRange * (72/80)      wept.WalkSpeed = SPEED_FAST     wept.RegionHeadMeleeMulti = 1.35   function wept:OnAssist(zombie)  local assister = self:GetOwner()   if assister:IsValid() then  self:SetWeaponBuildup(math.min(1, self:GetWeaponBuildup() + 0.06))  end  end      wept.WeaponBuildup = {  Colour = {150, 150, 30, 255},  Name = "Assists"  }      function wept:OnMeleeHit(hitent, hitflesh, tr)         local owner = self:GetOwner()          if SERVER and hitent:IsValidLivingZombie() and gamemode.Call("PlayerShouldTakeDamage", hitent, owner) then             local chance = math.random(9)              if chance == 1 then                 hitent:AddBurnDamage(45, attacker)             elseif chance == 2 then                 hitent:AddLegDamageExt(16, owner, self, SLOWTYPE_PULSE)             elseif chance == 3 then                 hitent:AddLegDamageExt(16, owner, self, SLOWTYPE_COLD)             end         end     end      function wept:PreDrawViewModel(vm)         if self:GetOwner().SwordThrow then return true end          BaseClass.PreDrawViewModel(self, vm)     end end) branch.CollectiveName = "Procyon" branch.Killicon = "weapon_zs_procyon"  function SWEP:PlaySwingSound()  self:EmitSound("weapons/iceaxe/iceaxe_swing1.wav", 75, math.random(30, 40)) end  function SWEP:PlayHitSound()  self:EmitSound("physics/metal/metal_box_impact_bullet1.wav", 75, math.random(30, 40))  self:EmitSound("weapons/crowbar/crowbar_impact1.wav", 75, math.Rand(40, 50), nil, CHAN_AUTO) end  function SWEP:PlayHitFleshSound()  self:EmitSound("physics/body/body_medium_break"..math.random(2, 4)..".wav", 75, math.Rand(86, 90), nil, CHAN_AUTO) end  function SWEP:OnMeleeHit(hitent, hitflesh, tr)  local heavy = self:IsHeavy()  if heavy and hitent:IsValidLivingZombie() and gamemode.Call("PlayerShouldTakeDamage", hitent, self:GetOwner()) then  local wep = hitent:GetActiveWeapon()  if wep:IsValid() and wep.StopSwinging then  wep:StopSwinging()  end   for i = 0, 10 do  timer.Simple(i * 0.01, function()  hitent:AnimSetGestureWeight(GESTURE_SLOT_ATTACK_AND_RELOAD, 1 - i * 0.07)  end)  end  end end