AddCSLuaFile()  local BaseClass = baseclass.Get("weapon_zs_fastzombie")  SWEP.PrintName = "Violator"  SWEP.ViewModel = Model("models/weapons/v_nFZA.mdl") SWEP.WorldModel = ""  if CLIENT then  SWEP.ViewModelFOV = 70 end  SWEP.MeleeDamage = 9 SWEP.SlowMeleeDamage = 21 SWEP.PounceDamage = 30  AccessorFuncDT(SWEP, "Tormented", "Float", 5)  function SWEP:PlaySlowSwingSound()  self:EmitSound("npc/fast_zombie/leap1.wav", 75, 78) end  function SWEP:PlayPounceSound()  self:EmitSound("npc/ichthyosaur/attack_growl1.wav", 75, 136, nil, CHAN_AUTO) end  function SWEP:ApplyMeleeDamage(ent, trace, damage)  if SERVER and ent:IsPlayer() then  ent:AddWounds(math.ceil(damage/2))  end   BaseClass.ApplyMeleeDamage(self, ent, trace, damage) end  if CLIENT then return end  function SWEP:Deploy()  self:GetOwner():CreateAmbience("violatorambience")   return BaseClass.Deploy(self) end 