SWEP.PrintName = "'Zeus' Voltaic Crossbow" SWEP.Description = "An arcing crossbow that fires bolts as it travels from the front of the arrow, building up energy. Alt fire uses energy to fire bolts that arc as they fly."  SWEP.Base = "weapon_zs_baseproj"  SWEP.HoldType = "crossbow"  SWEP.ViewModel = "models/weapons/c_crossbow.mdl" SWEP.WorldModel = "models/weapons/w_crossbow.mdl" SWEP.UseHands = true  SWEP.CSMuzzleFlashes = false  SWEP.Primary.Sound = Sound("ambient/levels/labs/electric_explosion5.wav") SWEP.ReloadFinishSound = Sound("npc/vort/attack_shoot.wav") SWEP.Primary.Delay = 1 SWEP.Primary.Automatic = true SWEP.Primary.Damage = 95  SWEP.Primary.ClipSize = 3 SWEP.Primary.Ammo = "XBowBolt" SWEP.Primary.DefaultClip = 15 SWEP.RequiredClip = 3  SWEP.SecondaryDelay = 0.25  SWEP.WalkSpeed = SPEED_SLOW  SWEP.Tier = 6 SWEP.MaxStock = 2  SWEP.ConeMax = 1.6 SWEP.ConeMin = 1.2  SWEP.NextZoom = 0  SWEP.ReloadSpeed = 0.825 SWEP.PlayerReloadSpeedEffectiveness = 0.5  SWEP.WeaponBuildup = {     Colour = {180, 220, 255, 255},     Name = "Bolt Hits" }  SWEP.Arcs = 5  SWEP.ResistanceAmmoAs = "none"  GAMEMODE:AttachWeaponModifier(SWEP, WEAPON_MODIFIER_RELOAD_SPEED, 0.045, 1) GAMEMODE:AddNewRemantleBranch(SWEP, 1, "'Jupiter' Arc Projector", "Has a moderate clip size and fires single ammo bolts more rapidly, which penetrate and arc through targets", function(wept)     wept.Primary.Damage = wept.Primary.Damage * (40/95)     wept.ReloadSpeed = wept.ReloadSpeed * (0.66/0.825)     wept.RequiredClip = 1     wept.Primary.Delay = 0.5     wept.Primary.ClipSize = 5     wept.Primary.Projectile = "projectile_arrow_inq"     wept.Primary.ProjVelocity = 1600      wept.ConeMax = 2.5     wept.Pierces = 6     wept.Arcs = 1      wept.PlayerReloadSpeedEffectiveness = nil      wept.BeamColor = Color(255, 110, 110)     wept.BoltColor = Color(255, 70, 70)      function wept:EntModify(ent)         ent:SetDTInt(0, 1)     end      if CLIENT then         wept.VElements["electric_shit2++"].color = Color(50, 