AddCSLuaFile()  SWEP.PrintName = "Tar Zombie"  SWEP.Base = "weapon_zs_zombie"  SWEP.MeleeDamage = 28  SWEP.AlertDelay = 2.75  SWEP.PassiveMoan = false  function SWEP:PlayAttackSound()  self:EmitSound("npc/zombie/zo_attack"..math.random(2)..".wav", 70, math.random(87, 92)) end  function SWEP:PlayAlertSound()  self:GetOwner():EmitSound("npc/zombie/zombie_alert"..math.random(3)..".wav", 70, math.random(87, 92)) end  function SWEP:PlayIdleSound()  self:GetOwner():EmitSound("npc/zombie/zombie_voice_idle"..math.random(14)..".wav", 70, math.random(87, 92)) end  if not CLIENT then return end  local matTar = CreateMaterial("tarzombie", "VertexLitGeneric", {  ["$basetexture"] = "models/Zombie_Classic/Zombie_Classic_sheet",  ["$normalmapalphaenvmapmask"] = 1,  ["$bumpmap"] = "models/flesh_nrm",  ["$halflambert"] = 1,  ["$phong"] = 1,  ["$phongboost"] = 1,  ["$phongfresnelranges"] = "[1 2.5 5]",  ["$phongexponent"] = 50 })  function SWEP:ViewModelDrawn()  render.ModelMaterialOverride()  render.SetColorModulation(1, 1, 1) end  function SWEP:PreDrawViewModel(vm)  render.ModelMaterialOverride(matTar)  render.SetColorModulation(25/255, 25/255, 25/255) end 