CLASS.Base = "shadow_lurker"  CLASS.Name = "Abyss Lurker" CLASS.TranslationName = "class_abyss_lurker" CLASS.Description = "description_abyss_lurker" CLASS.Help = "controls_abyss_lurker"  CLASS.SWEP = "weapon_zs_abysslurker"  CLASS.Wave = 4 / 6  CLASS.Health = 245 CLASS.Speed = 185 CLASS.JumpPower = 160  CLASS.KnockbackScale = 0.25  CLASS.Points = CLASS.Health/GM.TorsoZombiePointRatio CLASS.Cat = ZCLASSCAT_SUPPORT  if not CLIENT then return end  CLASS.Icon = "zombiesurvival/killicons/skeletal_lurker" CLASS.IconColor = Color(20, 60, 20)  local render_SetBlend = render.SetBlend local render_SetColorModulation = render.SetColorModulation local render_SetMaterial = render.SetMaterial local render_DrawSprite = render.DrawSprite local render_ModelMaterialOverride = render.ModelMaterialOverride local angle_zero = angle_zero local LocalToWorld = LocalToWorld  local colGlow = Color(0, 255, 0) local matGlow = Material("sprites/glow04_noz") local matBlack = CreateMaterial("shadowlurkersheet", "UnlitGeneric", {["$basetexture"] = "Tools/toolsblack", ["$model"] = 1}) local vecEyeLeft = Vector(5, -3.5, -1) local vecEyeRight = Vector(5, -3.5, 1)  function CLASS:PrePlayerDraw(pl)  render_SetBlend(0.95)  render_SetColorModulation(0.1, 0.1, 0.1) end  function CLASS:PostPlayerDraw(pl)  render_SetBlend(1)  render_SetColorModulation(1, 1, 1) end  function CLASS:PrePlayerDrawOverrideModel(pl)  render_ModelMaterialOverride(matBlack) end  function CLASS:PostPlayerDrawOverrideModel(pl)  render_ModelMaterialOverride(nil)   if pl == MySelf and not pl:ShouldDrawLocalPlayer() or pl.SpawnProtection then return end   local pos, ang = pl:GetBonePositionMatrixed(5)  if pos then  render_SetMaterial(matGlow)  render_DrawSprite(LocalToWorld(vecEyeLeft, angle_zero, pos, ang), 16, 2, colGlow)  render_DrawSprite(LocalToWorld(vecEyeRight, angle_zero, pos, ang), 16, 2, colGlow)  end end 