CLASS.Base = "zombie"  CLASS.Name = "Corruptor" CLASS.TranslationName = "class_corruptor" CLASS.Description = "description_corruptor" CLASS.Help = "controls_corruptor"  CLASS.Demiboss = true  CLASS.Health = 735 CLASS.Speed = 180  CLASS.KnockbackScale = 0.5  CLASS.FearPerInstance = 0.4  CLASS.Points = CLASS.Health/GM.HumanoidZombiePointRatio CLASS.Cat = ZCLASSCAT_SUPPORT CLASS.NoPlayerColor = true  CLASS.BloodColor = -1  CLASS.SWEP = "weapon_zs_corruptor"  CLASS.CanFeignDeath = false CLASS.Corruptive    = true  function CLASS:AltUse(pl) end  function CLASS:UpdateAnimation(pl, velocity, maxseqgroundspeed)  local wep = pl:GetActiveWeapon()  if not wep:IsValid() then return end   pl:SetPlaybackRate(1)   return true end  function CLASS:PlayerFootstep(pl, vFootPos, iFoot, strSoundName, fVolume, pFilter)  return true end  function CLASS:PlayerStepSoundTime(pl, iType, bWalking)  return 1000 end  function CLASS:CalcMainActivity(pl, velocity)  if velocity:Length2DSqr() <= 1 then  if pl:Crouching() and pl:OnGround() then  return ACT_HL2MP_IDLE_CROUCH_ZOMBIE, -1  end   return ACT_HL2MP_IDLE_ZOMBIE, -1  end   if pl:Crouching() and pl:OnGround() then  return ACT_HL2MP_WALK_CROUCH_ZOMBIE_05, -1  end   return ACT_HL2MP_WALK_ZOMBIE_04, -1 end   function CLASS:PlayPainSound(pl)  pl:EmitSound("physics/concrete/boulder_impact_hard1.wav", 75, math.Rand(220, 235))  pl.NextPainSound = CurTime() + 0.5   return true end  function CLASS:PlayDeathSound(pl)  return true end  if SERVER then  function CLASS:OnKilled(pl, attacker, inflictor, suicide, headshot, dmginfo)  local pos = pl:WorldSpaceCenter()   local effectdata = EffectData()  effectdata:SetOrigin(pos)  effectdata:SetNormal(pl:GetUp())  effectdata:SetEntity(pl)  util.Effect("explosion_corruptor", effectdata, nil, true)   return true  end      function CLASS:ReviveCallback(pl, attacker, dmginfo)  end end  if not CLIENT then return end  CLASS.Icon = "zombiesurvival/killicons/corruptor"  function CLASS:PrePlayerDraw(pl)  render.SetBlend(0) end  function CLASS:PostPlayerDraw(pl)  render.SetBlend(1) end