CLASS.Base = "elder_ghoul"  CLASS.Name = "Prime Ghoul" CLASS.TranslationName = "class_primeghoul" CLASS.Description = "description_primeghoul" CLASS.Help = "controls_noxiousghoul"  CLASS.Health = 800 CLASS.Speed = 180  CLASS.KnockbackScale = 0.5  CLASS.FearPerInstance = 0.4  CLASS.Variant = false  CLASS.Demiboss = true CLASS.Wave = 0  CLASS.Points = CLASS.Health/GM.HumanoidZombiePointRatio CLASS.Cat = ZCLASSCAT_SUPPORT CLASS.NoPlayerColor = true  CLASS.CanFeignDeath = false  CLASS.SWEP = "weapon_zs_primeghoul"  function CLASS:AltUse(pl) end  if not CLIENT then return end  CLASS.Icon = "zombiesurvival/killicons/ghoul" CLASS.IconColor = Color(230, 110, 110)  local render_SetMaterial = render.SetMaterial local render_DrawSprite = render.DrawSprite local angle_zero = angle_zero local LocalToWorld = LocalToWorld  local colGlow = Color(170, 55, 50) local matSkin = Material("Models/humans/corpse/corpse1.vtf") local matGlow = Material("effects/blueflare1") local vecEyeLeft = Vector(4, -4.6, -1) local vecEyeRight = Vector(4, -4.6, 1)  function CLASS:PrePlayerDraw(pl)  render.ModelMaterialOverride(matSkin)  render.SetColorModulation(0.95, 0.1, 0.1) end  function CLASS:PostPlayerDraw(pl)  render.ModelMaterialOverride()  render.SetColorModulation(1, 1, 1)   if pl == MySelf and not pl:ShouldDrawLocalPlayer() or pl.SpawnProtection then return end   local pos, ang = pl:GetBonePositionMatrixed(6)  if pos then  render_SetMaterial(matGlow)  render_DrawSprite(LocalToWorld(vecEyeLeft, angle_zero, pos, ang), 35, 0.75, colGlow)  render_DrawSprite(LocalToWorld(vecEyeLeft, angle_zero, pos, ang), 16, 16, colGlow)  render_DrawSprite(LocalToWorld(vecEyeRight, angle_zero, pos, ang), 35, 0.75, colGlow)  render_DrawSprite(LocalToWorld(vecEyeRight, angle_zero, pos, ang), 16, 16, colGlow)  end end 