CLASS.Base = "flesh_creeper"  CLASS.Name = "Flesh Weaver" CLASS.TranslationName = "class_flesh_weaver" CLASS.Description = "description_flesh_weaver" CLASS.Help = "controls_flesh_creeper"  CLASS.Wave = 4 / 6 CLASS.Unlocked = false  CLASS.Health = 270 CLASS.Speed = 215  CLASS.Points = CLASS.Health/GM.NoHeadboxZombiePointRatio CLASS.Cat = ZCLASSCAT_SUPPORT CLASS.SWEP = "weapon_zs_fleshweaver"  if not CLIENT then return end  CLASS.Icon = "zombiesurvival/killicons/fleshcreeper" CLASS.IconColor = Color(200, 80, 80)  local matSkin = Material("Models/Barnacle/barnacle_sheet") function CLASS:PrePlayerDraw(pl)  render.SetColorModulation(0.7, 0.3, 0.3)  render.ModelMaterialOverride(matSkin) end  function CLASS:PostPlayerDraw(pl)  render.SetColorModulation(1, 1, 1)  render.ModelMaterialOverride() end  function CLASS:ShouldDrawLocalPlayer()  return true end 