CLASS.Base = "zombie"  CLASS.Name = "Gunk Fiend" CLASS.TranslationName = "class_gunk_fiend" CLASS.Description = "description_gunk_fiend" CLASS.Help = "controls_gunk_fiend"  CLASS.Wave = 3 / 6  CLASS.BetterVersion = "Tar Monster" CLASS.Health = 280 CLASS.HealthPerWave = 50 CLASS.Speed = 155  CLASS.Points = CLASS.Health/GM.HumanoidZombiePointRatio CLASS.Cat = ZCLASSCAT_SUPPORT  CLASS.SWEP = "weapon_zs_gunkfiend"  CLASS.Model = Model("models/player/zombie_fast.mdl")  local math_random = math.random local string_format = string.format  function CLASS:Move(pl, move)  local wep = pl:GetActiveWeapon()  if not wep.GetGunking then return end   if CurTime() < wep:GetGunking() + 2 then         local new_max_speed = move:GetMaxSpeed() + 30   move:SetMaxSpeed(new_max_speed)  move:SetMaxClientSpeed(new_max_speed)  end end  function CLASS:PlayPainSound(pl)  pl:EmitSound(string_format("npc/zombie_poison/pz_idle%d.wav", math_random(2, 3)), 72, 75)  pl.NextPainSound = CurTime() + 0.5   return true end  function CLASS:PlayDeathSound(pl)  pl:EmitSound("npc/headcrab_fast/die"..math.random(2)..".wav", 72, 60)   return true end  local function CreateGunk(pl, damage, damagepos, damagedir)  damage = math.min(damage, 100)   pl:EmitSound("physics/flesh/flesh_squishy_impact_hard"..math.random(4)..".wav", 74, 125 - damage * 0.50)   if SERVER then  damagepos = pl:LocalToWorld(damagepos)   for i=1, math.max(1, math.floor(damage / 15)) do  local ent = ents.Create("projectile_gunk")  if ent:IsValid() then  local heading = (damagedir + VectorRand() * 0.3):GetNormalized()  ent:SetPos(damagepos + heading)  ent:SetOwner(pl)  ent:Spawn()   local phys = ent:GetPhysicsObject()  if phys:IsValid() then  phys:Wake()  phys:SetVelocityInstantaneous(math.min(300, 50 + damage ^ math.Rand(1.15, 1.25)) * heading)  end  end  end  end end  function CLASS:ProcessDamage(pl, dmginfo)  local attacker, damage = dmginfo:GetAttacker(), math.min(dmginfo:GetDamage(), pl:Health())  if attacker ~= pl and damage >= 5 and CurTime() >= (pl.m_NextPukeEmit or 0) then  pl.m_NextPukeEmit = CurTime() + 0.3   local pos = pl:WorldToLocal(dmginfo:GetDamagePosition())  local norm = dmginfo:GetDamageForce():GetNormalized() * -1  timer.Simple(0, function()  if pl:IsValid() then  CreateGunk(pl, damage, pos, norm)  end  end)  end end  CLASS.ReviveCallback = function()     return false end