CLASS.Base = "fast_zombie"  CLASS.Name = "Violator" CLASS.TranslationName = "class_violator" CLASS.Description = "description_violator" CLASS.Help = "controls_fast_zombie"  CLASS.Model = Model("models/player/zombie_fast.mdl") CLASS.OverrideModel = Model("models/Zombie/Poison.mdl")  CLASS.Wave = 5 / 6 CLASS.Revives = true  CLASS.Health = 240 CLASS.HealthPerWave = 30 CLASS.SWEP = "weapon_zs_violator"  CLASS.Points = CLASS.Health/GM.NoHeadboxZombiePointRatio CLASS.Cat = ZCLASSCAT_ASSAULT  CLASS.CanTaunt = true  CLASS.PainSounds = {"NPC_FastZombie.Pain"} CLASS.DeathSounds = {"npc/fast_zombie/leap1.wav"}  CLASS.VoicePitch = 0.75  CLASS.NoFallDamage = true CLASS.NoFallSlowdown = true  if SERVER then  function CLASS:ReviveCallback(pl, attacker, dmginfo)  return false  end   function CLASS:ProcessDamage(pl, dmginfo)  local activ = pl:GetActiveWeapon()  if not activ.GetTormented then return end   local attacker, dmg = dmginfo:GetAttacker(), dmginfo:GetDamage()  if not attacker:IsValidLivingHuman() or CurTime() < activ:GetTormented() + 8 then return end   if pl:Health() - dmg < 150 then  for i = 0, 3 do  timer.Simple(0.04 * i,  function() if pl:IsValidLivingZombie() then pl:EmitSound("npc/fast_zombie/leap1.wav", 75, 60 + i*12, 0.4, CHAN_WEAPON + i) end  end)  end   activ:SetTormented(CurTime())  end  end end  if SERVER then return end  CLASS.Icon = "zombiesurvival/killicons/fastzombie" CLASS.IconColor = Color(135, 90, 50)  local matSkin = Material("models/zombie_fast/fast_zombie_sheet.vtf") function CLASS:PrePlayerDrawOverrideModel(pl)  render.ModelMaterialOverride(matSkin)  render.SetColorModulation(0.7, 0.4, 0.35) end  function CLASS:PostPlayerDrawOverrideModel(pl)  render.ModelMaterialOverride(nil)  render.SetColorModulation(1, 1, 1) end 